import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import HomePageNavbar from './components/HomePageNavbar';
import Home from './pages/Home';
import About from './pages/About';
import Login from './pages/Login';
import Register from './pages/Register';
import AdminLogin from './pages/AdminLogin';
import AdminDashboard from './pages/AdminDashboard'; // Add this import
import './App.css';

function App() {
  const token = localStorage.getItem('token');
  let userType = null;

  if (token) {
    try {
      const decodedToken = JSON.parse(atob(token.split('.')[1])); // Decode JWT to get user data
      userType = decodedToken.userType;
    } catch (e) {
      console.error('Invalid token:', e);
    }
  }

  return (
    <Router>
      <div className="App">
        <HomePageNavbar />
        <Routes>
          {/* Public Routes */}
          <Route path="/" element={<Home />} />
          <Route path="/about" element={<About />} />
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />

          {/* Admin Route - Protected */}
          <Route 
            path="/admin-login" 
            element={<AdminLogin />} 
          />
          <Route 
            path="/admin" 
            element={userType === 'admin' ? <AdminDashboard /> : <Navigate to="/admin-login" />} 
          />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
