// src/components/UserNavbar.jsx
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import '../styles/UserNavbar.css';

const UserNavbar = () => {
  const [dropdownOpen, setDropdownOpen] = useState(false);
  
  const categories = ['Electronics', 'Personal Items', 'IDs', 'Umbrellas & Water Bottles', 'Clothing', 'Other'];

  const toggleDropdown = () => {
    setDropdownOpen(!dropdownOpen);
  };

  return (
    <nav className="user-navbar">
      <div className="navbar-logo">
        <h1>BukSU Lost and Found</h1>
      </div>
      <div className="navbar-links">
        <ul>
          <li><Link to="/user-home">Home</Link></li>
          
          {/* Dropdown for Item Categories */}
          <li className="dropdown" onClick={toggleDropdown}>
            <span>Item Categories</span>
            {dropdownOpen && (
              <ul className="dropdown-menu">
                {categories.map((category) => (
                  <li key={category}><Link to={`/category/${category.toLowerCase().replace(/ & /g, '-').replace(/ /g, '-')}`}>{category}</Link></li>
                ))}
              </ul>
            )}
          </li>

          <li><Link to="/about">About Us</Link></li>
          <li><Link to="/contact">Contact Us</Link></li>
          
          {/* Profile and Logout */}
          <li><Link to="/profile"><img src="user-icon.png" alt="Profile" /></Link></li>
          <li><Link to="/logout">Logout</Link></li>
        </ul>
      </div>
    </nav>
  );
};

export default UserNavbar;
