import React, { useState, useEffect } from "react";
import axios from "axios";
import "../styles/AdminDashboard.css";

const AdminDashboard = () => {
  const [users, setUsers] = useState([]);
  const [newUser, setNewUser] = useState({ email: "", password: "", userType: "student" });
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    setLoading(true);
    setError(null);
    try {
      const token = localStorage.getItem("authToken");
      if (!token) {
        throw new Error("No token found. Please log in again.");
      }

      const response = await axios.get("http://localhost:5000/api/users/all-users", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUsers(response.data);
    } catch (err) {
      console.error("Error fetching users:", err.response?.data || err.message);
      setError(err.response?.data?.message || "Failed to fetch users.");
    } finally {
      setLoading(false);
    }
  };

  const addUser = async () => {
    setError(null);
    try {
      const token = localStorage.getItem("authToken");
      if (!newUser.email || !newUser.password || !newUser.userType) {
        alert("All fields are required.");
        return;
      }

      const response = await axios.post(
        "http://localhost:5000/api/auth/register",
        newUser,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      alert(response.data.message);
      setNewUser({ email: "", password: "", userType: "student" });
      fetchUsers(); // Refresh user list
    } catch (err) {
      console.error("Error adding user:", err.response?.data || err.message);
      alert(err.response?.data?.message || "Failed to add user.");
    }
  };

  const deleteUser = async (userId) => {
    try {
      const token = localStorage.getItem("authToken");
      const response = await axios.delete(`http://localhost:5000/api/users/${userId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      alert(response.data.message);
      fetchUsers(); // Refresh user list
    } catch (err) {
      console.error("Error deleting user:", err.response?.data || err.message);
      alert(err.response?.data?.message || "Failed to delete user.");
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("authToken");
    window.location.href = "/login"; // Redirect to login page
  };

  return (
    <div className="admin-dashboard">
      <nav className="navbar">
        <div className="navbar-brand">Admin Dashboard</div>
        <ul className="navbar-links">
          <li>
            <div className="dropdown">
              <button className="dropdown-btn">Category</button>
              <div className="dropdown-content">
                <a href="/category/1">Category 1</a>
                <a href="/category/2">Category 2</a>
                <a href="/category/3">Category 3</a>
              </div>
            </div>
          </li>
          <li>
            <a href="/lost-and-found">Lost and Found Items</a>
          </li>
          <li>
            <button className="logout-btn" onClick={handleLogout}>
              Logout
            </button>
          </li>
        </ul>
      </nav>

      <div className="content">
        <h1>Admin Dashboard</h1>

        <div className="add-user-form">
          <h2>Add New User</h2>
          <input
            type="email"
            placeholder="Email"
            value={newUser.email}
            onChange={(e) => setNewUser({ ...newUser, email: e.target.value })}
          />
          <input
            type="password"
            placeholder="Password"
            value={newUser.password}
            onChange={(e) => setNewUser({ ...newUser, password: e.target.value })}
          />
          <select
            value={newUser.userType}
            onChange={(e) => setNewUser({ ...newUser, userType: e.target.value })}
          >
            <option value="student">Student</option>
            <option value="staff">Staff</option>
            <option value="faculty">Faculty</option>
            <option value="admin">Admin</option>
          </select>
          <button onClick={addUser}>Add User</button>
        </div>

        <div className="registered-users">
          <h2>Registered Users</h2>
          {loading && <p>Loading users...</p>}
          {error && <p className="error-message">{error}</p>}
          {!loading && !error && users.length === 0 && <p>No registered users found.</p>}
          {!loading && users.length > 0 && (
            <div className="table-container">
              <table>
                <thead>
                  <tr>
                    <th>Email</th>
                    <th>User Type</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {users.map((user) => (
                    <tr key={user._id}>
                      <td>{user.email}</td>
                      <td>{user.userType}</td>
                      <td>
                        <button>
                        <i class="fa-solid fa-pen-to-square"></i>
                        </button>
                        <button onClick={() => deleteUser(user._id)}>
                          <i className="fas fa-trash"></i>
                        </button>
                        <button>
                          <a href={`mailto:${user.email}`} className="email-icon">
                            <i className="fas fa-envelope"></i>
                          </a>
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
