import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import UserNavbar from '../components/UserNavbar';
import '../styles/PostItemPage.css';

const PostItemPage = () => {
  const [image, setImage] = useState(null);
  const [description, setDescription] = useState('');
  const [category, setCategory] = useState('');
  const [error, setError] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccessMessage('');

    // Basic form validation
    if (!image || !description || !category) {
      setError('All fields are required.');
      return;
    }

    const formData = new FormData();
    formData.append('image', image);
    formData.append('description', description);
    formData.append('category', category);

    try {
      const token = localStorage.getItem('token');
      const response = await axios.post('http://localhost:5000/api/items', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
          Authorization: `Bearer ${token}`,
        },
      });
      setSuccessMessage(response.data.message || 'Item posted successfully!');
      setTimeout(() => navigate('/profile'), 2000); // Redirect after 2 seconds
    } catch (error) {
      console.error('Error posting item:', error);
      setError('Failed to post the item. Please try again.');
    }
  };

  return (
    <div className="PostItemPage">
      <UserNavbar />
      <div className="post-item-page">
        <h2>Post an Item</h2>
        <form onSubmit={handleSubmit} className="post-item-form">
          {error && <p className="error">{error}</p>} {/* Display error message */}
          {successMessage && <p className="success">{successMessage}</p>} {/* Display success message */}

          <label>
            Upload Image:
            <input
              type="file"
              accept="image/*"
              onChange={(e) => setImage(e.target.files[0])}
              required
            />
          </label>

          <label>
            Description:
            <textarea
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              placeholder="Enter a description for the item"
              required
            />
          </label>

          <label>
            Category:
            <select
              value={category}
              onChange={(e) => setCategory(e.target.value)}
              required
            >
              <option value="">Select a category</option>
              <option value="Electronics">Electronics</option>
              <option value="Personal-item">Personal Item</option>
              <option value="ID">ID</option>
              <option value="Other">Other</option>
            </select>
          </label>

          <button type="submit">Post Item</button>
        </form>
      </div>
    </div>
  );
};

export default PostItemPage;
