import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/AdminDashboard.css'

const AdminDashboard = () => {
  const [users, setUsers] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        // Retrieve token from localStorage
        const token = localStorage.getItem('token'); // Updated key to match 'token'
  
        if (!token) {
          setError('No token found. Please log in again.');
          return;
        }
  
        console.log('Token from localStorage:', token); // Debugging log
  
        // Make the request with Authorization header containing the token
        const response = await axios.get('http://localhost:5000/api/users/all-users', {
          headers: { Authorization: `Bearer ${token}` },
        });
  
        console.log('Fetched users:', response.data); // Debugging log
        setUsers(response.data);
      } catch (err) {
        console.error('Error fetching users:', err);
        setError('Error fetching users');
      } finally {
        setLoading(false);
      }
    };
  
    fetchUsers();
  }, []);
  

  const renderContent = () => {
    if (loading) {
      return <p>Loading users...</p>;
    }

    if (error) {
      return <p className="error">{error}</p>;
    }

    if (users.length === 0) {
      return <p>No users found in the database.</p>;
    }

    return (
      <table className="users-table">
        <thead>
          <tr>
            <th>Email</th>
            <th>User Type</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user._id}>
              <td>{user.email}</td>
              <td>{user.userType}</td>
            </tr>
          ))}
        </tbody>
      </table>
    );
  };

  return (
    <div className="admin-dashboard">
      <h1>Admin Dashboard</h1>
      {renderContent()}
    </div>
  );
};

export default AdminDashboard;
