import React, { useState } from 'react';
import { GoogleLogin } from '@react-oauth/google';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../styles/Login.css';

const Login = () => {
  const [loginData, setLoginData] = useState({ email: '', password: '' });
  const navigate = useNavigate();

  const handleChange = (e) => {
    setLoginData({ ...loginData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const endpoint = 'http://localhost:5000/api/auth/login';
      const res = await axios.post(endpoint, loginData);
      const { token, userType } = res.data;

      if (token) {
        localStorage.setItem('token', token);
        userType === 'admin' ? navigate('/admin') : navigate('/user-home');
      } else {
        alert('Login failed: Invalid response from server.');
      }
    } catch (error) {
      console.error('Error:', error.response?.data || error.message);
      alert('Login failed: ' + (error.response?.data?.message || error.message));
    }
  };

  const handleGoogleLoginSuccess = async (response) => {
    try {
      const googleToken = response.credential;
      const res = await axios.post('http://localhost:5000/api/auth/google', { token: googleToken });
      const { success, token, userType } = res.data;

      if (success) {
        localStorage.setItem('token', token);
        userType === 'admin' ? navigate('/admin') : navigate('/user-home');
      } else {
        alert('Google login failed.');
      }
    } catch (error) {
      console.error('Google login error:', error.response?.data || error.message);
      alert('Google login failed.');
    }
  };

  return (
    <div className="login-page">
      <div className="login-wrapper">
        {/* Left Side */}
        <div className="login-left">
          <div className="logo">X</div>
        </div>

        {/* Right Side */}
        <div className="login-right">
          <h1>Happening now</h1>
          <h2>Join today.</h2>
          <GoogleLogin
            onSuccess={handleGoogleLoginSuccess}
            onError={() => console.log('Google Login Failed')}
          />
          <div className="divider">or</div>

          <form onSubmit={handleSubmit}>
            <input
              type="email"
              name="email"
              placeholder="Email"
              value={loginData.email}
              onChange={handleChange}
              required
            />
            <input
              type="password"
              name="password"
              placeholder="Password"
              value={loginData.password}
              onChange={handleChange}
              required
            />
            <button type="submit" className="create-account">Login</button>
          </form>

          <p className="terms">
            By signing up, you agree to the <a href="#">Terms of Service</a> and <a href="#">Privacy Policy</a>.
          </p>
          <p className="signin">
            Already have an account? <a href="#" className="signin-link">Sign in</a>
          </p>
        </div>
      </div>
    </div>
  );
};

export default Login;
