import React, { useState, useEffect } from "react";
import axios from "axios";
import "../styles/AdminDashboard.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faUserPlus,
  faUsers,
  faTrash,
  faEdit,
  faEnvelope,
  faMagnifyingGlass,
  faBoxOpen,
  faBell,
  faRightFromBracket,
} from "@fortawesome/free-solid-svg-icons";

const AdminDashboard = () => {
  const [currentView, setCurrentView] = useState("dashboard");
  const [users, setUsers] = useState([]);
  const [items, setItems] = useState([]);
  const [notifications, setNotifications] = useState([]);
  const [categoryFilter, setCategoryFilter] = useState("All");
  const [newUser, setNewUser] = useState({
    firstname: "",
    lastname: "",
    email: "",
    password: "",
    userType: "student",
  });
  const [editingUser, setEditingUser] = useState(null);

  useEffect(() => {
    if (currentView === "registeredUsers") fetchUsers();
    if (currentView === "lostItems" || currentView === "foundItems") fetchItems();
    if (currentView === "inbox") fetchNotifications();
  }, [currentView, categoryFilter]);

  const fetchUsers = async () => {
    const token = localStorage.getItem("authToken");
    const res = await axios.get("http://localhost:5000/api/users/all-users", {
      headers: { Authorization: `Bearer ${token}` },
    });
    setUsers(res.data);
  };

  const fetchItems = async () => {
    const token = localStorage.getItem("authToken");
    const res = await axios.get("http://localhost:5000/api/items", {
      headers: { Authorization: `Bearer ${token}` },
    });
    const filteredItems = res.data.filter(
      (item) =>
        (currentView === "lostItems" ? item.postType === "Lost" : item.postType === "Found") &&
        (categoryFilter === "All" || item.category === categoryFilter)
    );
    setItems(filteredItems);
  };

  const fetchNotifications = async () => {
    const token = localStorage.getItem("authToken");
    const res = await axios.get("http://localhost:5000/api/contact/inbox", {
      headers: { Authorization: `Bearer ${token}` },
    });
    setNotifications(res.data);
  };

  const renderView = () => {
    switch (currentView) {
      case "dashboard":
        return <h2>Welcome to Admin Dashboard</h2>;
      case "addUser":
        return (
          <div className="centered-view">
            <h2>Add New User</h2>
            <form>
              <input
                type="text"
                placeholder="First Name"
                onChange={(e) => setNewUser({ ...newUser, firstname: e.target.value })}
              />
              <input
                type="text"
                placeholder="Last Name"
                onChange={(e) => setNewUser({ ...newUser, lastname: e.target.value })}
              />
              <input
                type="email"
                placeholder="Email"
                onChange={(e) => setNewUser({ ...newUser, email: e.target.value })}
              />
              <input
                type="password"
                placeholder="Password"
                onChange={(e) => setNewUser({ ...newUser, password: e.target.value })}
              />
              <button>Add User</button>
            </form>
          </div>
        );
      case "registeredUsers":
        return (
          <div className="centered-view">
            <h2>Registered Users</h2>
            <ul>
              {users.map((user) => (
                <li key={user._id}>
                  {user.firstname} {user.lastname} - {user.email}
                </li>
              ))}
            </ul>
          </div>
        );
      case "lostItems":
      case "foundItems":
        return (
          <div className="centered-view">
            <h2>{currentView === "lostItems" ? "Lost Items" : "Found Items"}</h2>
            <select onChange={(e) => setCategoryFilter(e.target.value)}>
              <option>All</option>
              <option>Electronics</option>
              <option>Clothing</option>
            </select>
            <ul>
              {items.map((item) => (
                <li key={item._id}>{item.description}</li>
              ))}
            </ul>
          </div>
        );
      case "inbox":
        return (
          <div className="centered-view">
            <h2>Inbox</h2>
            {notifications.map((msg, i) => (
              <div key={i}>{msg.message}</div>
            ))}
          </div>
        );
      default:
        return <h2>Select a View</h2>;
    }
  };

  return (
    <div className="admin-dashboard">
      <div className="sidebar">
        <ul>
          <li onClick={() => setCurrentView("dashboard")}>Dashboard</li>
          <li onClick={() => setCurrentView("addUser")}>Add User</li>
          <li onClick={() => setCurrentView("registeredUsers")}>Registered Users</li>
          <li onClick={() => setCurrentView("lostItems")}>Lost Items</li>
          <li onClick={() => setCurrentView("foundItems")}>Found Items</li>
          <li onClick={() => setCurrentView("inbox")}>Inbox</li>
        </ul>
      </div>
      <div className="content">{renderView()}</div>
    </div>
  );
};

export default AdminDashboard;
