import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/UserHomePage.css'; // Custom styles for the page

const UserHome = () => {
  const [items, setItems] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchPostedItems();
  }, []);

  const fetchPostedItems = async () => {
    try {
      const token = localStorage.getItem('token'); // Retrieve the token from local storage
      if (!token) {
        throw new Error('No token found. Please log in again.');
      }

      const response = await axios.get('http://localhost:5000/api/items', {
        headers: { Authorization: `Bearer ${token}` },
      });

      setItems(response.data);
    } catch (err) {
      console.error('Error fetching posted items:', err.response?.data || err.message);
      setError('Failed to load items. Please try again.');
    }
  };

  return (
    <div className="user-home">
      <div className="feed-container">
        <h1 className="feed-title">Item Feed</h1>
        {error && <p className="error-message">{error}</p>}
        <div className="feed-items">
          {items.length === 0 ? (
            <p>No items to display.</p>
          ) : (
            items.map((item) => (
              <div key={item._id} className="feed-item">
                <img
                  src={`http://localhost:5000/uploads/${item.image}`}
                  alt={item.description}
                  className="feed-item-image"
                />
                <div className="feed-item-content">
                  <p className="feed-item-description">{item.description}</p>
                  <p className="feed-item-category">{item.category}</p>
                </div>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
};

export default UserHome;
