import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import '../styles/UserHomePage.css';

const UserHomePage = () => {
  const [items, setItems] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchAllItems();
  }, []);

  const fetchAllItems = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        throw new Error('No token found. Please log in again.');
      }

      const response = await axios.get('http://localhost:5000/api/items', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setItems(response.data);
    } catch (err) {
      console.error('Error fetching items:', err.response?.data || err.message);
      setError('Failed to fetch items. Please try again.');
    }
  };

  return (
    <div className="user-home">
      {/* Sidebar */}
      <div className="sidebar">
        <h2>Menu</h2>
        <ul>
          <li>Home</li>
          <li>Lost</li>
          <li>Found</li>
          <Link to="/notifications">Notifications</Link>
          <li>Bookmarks</li>
          <Link to="/profile">Profile</Link>
          <li>Settings</li>
        </ul>

        <Link to="/post-item">
          <button className="post-button">Post</button>
        </Link>
      </div>

      {/* Feed Section */}
      <div className="feed">
        <h1>All Items</h1>
        {error && <p className="error">{error}</p>}
        <div className="items-feed">
          {items.length === 0 ? (
            <p>No items have been posted yet.</p>
          ) : (
            items.map((item) => (
              <div className="item-post" key={item._id}>
                <div className="post-header">
                  <strong>
                    {item.postedBy
                      ? `${item.postedBy.firstname} ${item.postedBy.lastname}`
                      : 'Unknown'}
                  </strong>
                  <span> • {new Date(item.createdAt).toLocaleDateString()}</span>
                </div>
                <div className="post-body">
                  {item.image && (
                    <img
                      src={`http://localhost:5000/uploads/${item.image}`}
                      alt={item.description}
                      className="post-image"
                    />
                  )}
                  <p className="post-description">{item.description}</p>
                  <p className="post-category">
                    <strong>Category:</strong> {item.category}
                  </p>
                  <p className="post-type">
                    <strong>Post Type:</strong> {item.postType}
                  </p>
                </div>
              </div>
            ))
          )}
        </div>
      </div>

      {/* Right Sidebar */}
      <div className="right-sidebar">
        <h3>What’s happening</h3>
        <ul>
          <li>#TrendingTopic1</li>
          <li>#TrendingTopic2</li>
          <li>#TrendingTopic3</li>
        </ul>
        <h3>Who to follow</h3>
        <ul>
          <li>John Doe</li>
          <li>Jane Smith</li>
          <li>Example User</li>
        </ul>
      </div>
    </div>
  );
};

export default UserHomePage;
