import React, { useState, useEffect } from "react";
import axios from "axios";
import "../styles/AdminDashboard.css";

const AdminDashboard = () => {
  const [currentView, setCurrentView] = useState("registeredUsers");
  const [users, setUsers] = useState([]);
  const [items, setItems] = useState([]);
  const [newUser, setNewUser] = useState({
    firstname: "",
    lastname: "",
    email: "",
    password: "",
    userType: "student",
  });
  const [editingUser, setEditingUser] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (currentView === "registeredUsers") fetchUsers();
    if (currentView === "lostItems" || currentView === "foundItems") fetchItems();
  }, [currentView]);

  // Fetch Registered Users
  const fetchUsers = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem("authToken");
      const response = await axios.get("http://localhost:5000/api/users/all-users", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUsers(response.data);
    } catch (err) {
      setError("Failed to fetch users.");
    } finally {
      setLoading(false);
    }
  };

  // Fetch Lost/Found Items
  const fetchItems = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem("authToken");
      const response = await axios.get("http://localhost:5000/api/items", {
        headers: { Authorization: `Bearer ${token}` },
      });

      const filteredItems = response.data.filter(
        (item) => currentView === "lostItems" ? item.postType === "Lost" : item.postType === "Found"
      );

      setItems(filteredItems);
    } catch (err) {
      setError("Failed to fetch items.");
    } finally {
      setLoading(false);
    }
  };

  // Add New User
  const addUser = async () => {
    try {
      const token = localStorage.getItem("authToken");
      await axios.post("http://localhost:5000/api/auth/register", newUser, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert("User added successfully!");
      fetchUsers();
      setNewUser({ firstname: "", lastname: "", email: "", password: "", userType: "student" });
    } catch (err) {
      alert("Failed to add user.");
    }
  };

  // Delete User
  const deleteUser = async (userId) => {
    try {
      const token = localStorage.getItem("authToken");
      await axios.delete(`http://localhost:5000/api/users/${userId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchUsers();
    } catch (err) {
      alert("Failed to delete user.");
    }
  };

  // Save Updated User
  const updateUser = async () => {
    try {
      const token = localStorage.getItem("authToken");
      await axios.put(`http://localhost:5000/api/users/${editingUser._id}`, editingUser, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert("User updated successfully!");
      setEditingUser(null);
      fetchUsers();
    } catch (err) {
      alert("Failed to update user.");
    }
  };

  const renderView = () => {
    switch (currentView) {
      case "addUser":
        return (
          <div className="add-user-form">
            <h2>Add New User</h2>
            <input
              type="text"
              placeholder="First Name"
              value={newUser.firstname}
              onChange={(e) => setNewUser({ ...newUser, firstname: e.target.value })}
            />
            <input
              type="text"
              placeholder="Last Name"
              value={newUser.lastname}
              onChange={(e) => setNewUser({ ...newUser, lastname: e.target.value })}
            />
            <input
              type="email"
              placeholder="Email"
              value={newUser.email}
              onChange={(e) => setNewUser({ ...newUser, email: e.target.value })}
            />
            <input
              type="password"
              placeholder="Password"
              value={newUser.password}
              onChange={(e) => setNewUser({ ...newUser, password: e.target.value })}
            />
            <select
              value={newUser.userType}
              onChange={(e) => setNewUser({ ...newUser, userType: e.target.value })}
            >
              <option value="student">Student</option>
              <option value="staff">Staff</option>
              <option value="faculty">Faculty</option>
              <option value="admin">Admin</option>
            </select>
            <button onClick={addUser}>Add User</button>
          </div>
        );
      case "registeredUsers":
        return (
          <div className="registered-users">
            <h2>Registered Users</h2>
            <table>
              <thead>
                <tr>
                  <th>First Name</th>
                  <th>Last Name</th>
                  <th>Email</th>
                  <th>User Type</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {users.map((user) => (
                  <tr key={user._id}>
                    <td>{user.firstname}</td>
                    <td>{user.lastname}</td>
                    <td>{user.email}</td>
                    <td>{user.userType}</td>
                    <td>
                      <button onClick={() => setEditingUser(user)}>‚úèÔ∏è</button>
                      <button onClick={() => deleteUser(user._id)}>üóëÔ∏è</button>
                      <a
                        href={`mailto:${user.email}`}
                        title="Send Message"
                        target="_blank"
                        rel="noopener noreferrer"
                      >
                        ‚úâÔ∏è
                      </a>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        );
      case "lostItems":
      case "foundItems":
        return (
          <div className="items-display">
            {items.length === 0 ? (
              <p>No items available.</p>
            ) : (
              items.map((item) => (
                <div key={item._id} className="item-card">
                  {item.image && (
                    <img
                      src={`http://localhost:5000/uploads/${item.image}`}
                      alt={item.description}
                      className="item-image"
                    />
                  )}
                  <p>{item.description}</p>
                  <p>
                    <strong>Posted By:</strong>{" "}
                    {item.postedBy
                      ? `${item.postedBy.firstname} ${item.postedBy.lastname}`
                      : "Unknown"}
                  </p>
                </div>
              ))
            )}
          </div>
        );
      default:
        return <p>Select an option from the menu.</p>;
    }
  };
  

  return (
    <div className="admin-dashboard">
      <div className="left-sidebar">
        <h2>Admin Menu</h2>
        <ul>
          <li onClick={() => setCurrentView("addUser")}>Add User</li>
          <li onClick={() => setCurrentView("registeredUsers")}>Registered Users</li>
          <li onClick={() => setCurrentView("lostItems")}>Lost Items</li>
          <li onClick={() => setCurrentView("foundItems")}>Found Items</li>
          <li>Logout</li>
        </ul>
      </div>
      <div className="content">{renderView()}</div>
    </div>
  );
};

export default AdminDashboard;
