import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/UserHomePage.css'; // Import custom styles for the page

const UserHome = () => {
  const [items, setItems] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchPostedItems();
  }, []);

  const fetchPostedItems = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/items'); // Adjust API endpoint if needed
      setItems(response.data);
    } catch (err) {
      console.error('Error fetching posted items:', err);
      setError('Failed to load posted items. Please try again later.');
    }
  };

  return (
    <div className="user-home">
      <div className="user-home-container">
        <h1>Recent Posts</h1>
        {error && <p className="error-message">{error}</p>}

        <div className="items-feed">
          {items.length === 0 ? (
            <p>No items posted yet.</p>
          ) : (
            items.map((item) => (
              <div key={item._id} className="item-card">
                <div className="item-header">
                  <img
                    src={`http://localhost:5000/uploads/${item.image}`}
                    alt={item.description}
                    className="item-image"
                  />
                  <div className="item-details">
                    <h2 className="item-description">{item.description}</h2>
                    <p className="item-category">Category: {item.category}</p>
                  </div>
                </div>
                <div className="item-footer">
                  <p className="item-timestamp">Posted on: {new Date(item.createdAt).toLocaleString()}</p>
                </div>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
};

export default UserHome;
