import React, { useState, useEffect } from "react";
import axios from "axios";
import "../styles/AdminDashboard.css";

const AdminDashboard = () => {
  const [users, setUsers] = useState([]);
  const [newUser, setNewUser] = useState({
    firstname: "",
    lastname: "",
    email: "",
    password: "",
    userType: "student",
  });
  const [editingUser, setEditingUser] = useState(null); // Track the user being edited
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchUsers();
  }, []);

  // Fetch all registered users
  const fetchUsers = async () => {
    setLoading(true);
    setError(null);
    try {
      const token = localStorage.getItem("authToken");
      const response = await axios.get("http://localhost:5000/api/users/all-users", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUsers(response.data);
    } catch (err) {
      setError(err.response?.data?.message || "Failed to fetch users.");
    } finally {
      setLoading(false);
    }
  };

  const addUser = async () => {
    if (!newUser.firstname || !newUser.lastname || !newUser.email || !newUser.password) {
      alert("All fields are required.");
      return;
    }
    try {
      const token = localStorage.getItem("authToken");
      await axios.post("http://localhost:5000/api/auth/register", newUser, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert("User added successfully!");
      fetchUsers();
      setNewUser({ firstname: "", lastname: "", email: "", password: "", userType: "student" });
    } catch (err) {
      alert("Failed to add user.");
    }
  };

  const deleteUser = async (userId) => {
    try {
      const token = localStorage.getItem("authToken");
      await axios.delete(`http://localhost:5000/api/users/${userId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchUsers();
    } catch (err) {
      alert("Failed to delete user.");
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("authToken");
    window.location.href = "/login";
  };

  return (
    <div className="admin-dashboard">
      {/* Sidebar */}
      <div className="sidebar">
        <h2>Admin Menu</h2>
        <ul>
          <li>Add User</li>
          <li>Registered Users</li>
          <li>Categories</li>
          <li>Lost and Found Items</li>
          <li onClick={handleLogout} style={{ color: "red", cursor: "pointer" }}>
            Logout
          </li>
        </ul>
      </div>

      {/* Main Content */}
      <div className="content">
        <div className="add-user-form">
          <h2>Add New User</h2>
          <input
            type="text"
            placeholder="First Name"
            value={newUser.firstname}
            onChange={(e) => setNewUser({ ...newUser, firstname: e.target.value })}
          />
          <input
            type="text"
            placeholder="Last Name"
            value={newUser.lastname}
            onChange={(e) => setNewUser({ ...newUser, lastname: e.target.value })}
          />
          <input
            type="email"
            placeholder="Email"
            value={newUser.email}
            onChange={(e) => setNewUser({ ...newUser, email: e.target.value })}
          />
          <input
            type="password"
            placeholder="Password"
            value={newUser.password}
            onChange={(e) => setNewUser({ ...newUser, password: e.target.value })}
          />
          <select
            value={newUser.userType}
            onChange={(e) => setNewUser({ ...newUser, userType: e.target.value })}
          >
            <option value="student">Student</option>
            <option value="staff">Staff</option>
            <option value="faculty">Faculty</option>
            <option value="admin">Admin</option>
          </select>
          <button onClick={addUser}>Add User</button>
        </div>

        <div className="registered-users">
          <h2>Registered Users</h2>
          {loading ? (
            <p>Loading users...</p>
          ) : (
            <table>
              <thead>
                <tr>
                  <th>First Name</th>
                  <th>Last Name</th>
                  <th>Email</th>
                  <th>User Type</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {users.map((user) => (
                  <tr key={user._id}>
                    <td>{user.firstname}</td>
                    <td>{user.lastname}</td>
                    <td>{user.email}</td>
                    <td>{user.userType}</td>
                    <td>
                      <button onClick={() => deleteUser(user._id)}>
                        <i className="fa-solid fa-trash" style={{ color: "red" }}></i>
                      </button>
                      <a
                        href={`mailto:${user.email}?subject=Message from Admin`}
                        target="_blank"
                        rel="noopener noreferrer"
                      >
                        <i className="fa-solid fa-envelope" style={{ color: "green" }}></i>
                      </a>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>
      </div>

      {/* Right Sidebar */}
      <div className="right-sidebar">
        <h3>What's Happening</h3>
        <ul>
          <li>#AdminUpdates</li>
          <li>#LostAndFound</li>
          <li>#UserReports</li>
        </ul>
      </div>
    </div>
  );
};

export default AdminDashboard;
