import React from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom';
import HomePageNavbar from './components/HomePageNavbar';  // Import the homepage navbar
import UserNavbar from './components/UserNavbar';  // Import the user navbar
import Home from './pages/Home';
import About from './pages/About';
import Login from './pages/Login';
import Register from './pages/Register';
import UserHomePage from './pages/UserHomePage';  // Import UserHomePage
import ProtectedRoute from './components/ProtectedRoute';  // ProtectedRoute for login-check
import './App.css';

function App() {
  // Logout functionality
  const handleLogout = () => {
    // Remove user data from localStorage or sessionStorage
    localStorage.removeItem('user');

    // Redirect to login page
    window.location.href = '/login';
  };

  return (
    <Router>
      <div className="App">
        <Routes>
          {/* Public Routes */}
          <Route path="/" element={<><HomePageNavbar /><Home /></>} />
          <Route path="/about" element={<><HomePageNavbar /><About /></>} />
          <Route path="/login" element={<><HomePageNavbar /><Login /></>} />
          <Route path="/register" element={<><HomePageNavbar /><Register /></>} />

          {/* Protected Routes */}
          <Route 
            path="/user-home" 
            element={<><UserNavbar /><ProtectedRoute element={<UserHomePage />} /></>} 
          />

          {/* Handle logout by redirecting */}
          <Route 
            path="/logout" 
            element={<Logout handleLogout={handleLogout} />} 
          />
        </Routes>
      </div>
    </Router>
  );
}

// Logout component to handle logout redirection
const Logout = ({ handleLogout }) => {
  React.useEffect(() => {
    handleLogout(); // Perform the logout action
  }, [handleLogout]);

  return null; // Render nothing since we just redirect
};

export default App;
