import React, { useState, useEffect } from "react";
import axios from "axios";
import "../styles/AdminDashboard.css";

const AdminDashboard = () => {
  const [users, setUsers] = useState([]);
  const [newUser, setNewUser] = useState({
    firstname: "",
    lastname: "",
    email: "",
    password: "",
    userType: "student",
  });
  const [editingUser, setEditingUser] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
   const [items, setItems] = useState([]);
    const [filterType, setFilterType] = useState('All'); // For Lost/Found filter
     const [filterCategory, setFilterCategory] = useState('All'); // For Category filter

  useEffect(() => {
    fetchUsers();
    fetchAllItems();
  }, []);

  useEffect(() => {
    fetchAllItems();
  }, []);
  
  const fetchAllItems = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        throw new Error('No token found. Please log in again.');
      }
  
      const response = await axios.get('http://localhost:5000/api/items', {
        headers: { Authorization: `Bearer ${token}` },
      });
      console.log('Fetched Items:', response.data); // Debug log
      setItems(response.data);
    } catch (err) {
      console.error('Error fetching items:', err.response?.data || err.message);
      setError('Failed to fetch items. Please try again.');
    }
  };


  // Fetch all registered users
  const fetchUsers = async () => {
    setLoading(true);
    setError(null);
    try {
      const token = localStorage.getItem("authToken");
      if (!token) throw new Error("Authentication token not found. Please log in.");

      const response = await axios.get("http://localhost:5000/api/users/all-users", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUsers(response.data);
    } catch (err) {
      console.error("Error fetching users:", err.response?.data || err.message);
      setError(err.response?.data?.message || "Failed to fetch users.");
    } finally {
      setLoading(false);
    }
  };

  // Add a new user
  const addUser = async () => {
    setError(null);
    try {
      const token = localStorage.getItem("authToken");
      if (!newUser.firstname || !newUser.lastname || !newUser.email || !newUser.password) {
        alert("All fields are required.");
        return;
      }

      const response = await axios.post(
        "http://localhost:5000/api/auth/register",
        newUser,
        { headers: { Authorization: `Bearer ${token}` } }
      );

      alert(response.data.message || "User added successfully!");
      setNewUser({ firstname: "", lastname: "", email: "", password: "", userType: "student" });
      fetchUsers();
    } catch (err) {
      console.error("Error adding user:", err.response?.data || err.message);
      alert(err.response?.data?.message || "Failed to add user.");
    }
  };

  // Update an existing user
  const updateUser = async () => {
    try {
      if (!editingUser) return;

      const token = localStorage.getItem("authToken");
      const response = await axios.put(
        `http://localhost:5000/api/users/${editingUser._id}`,
        editingUser,
        { headers: { Authorization: `Bearer ${token}` } }
      );

      alert(response.data.message || "User updated successfully!");
      setEditingUser(null);
      fetchUsers();
    } catch (err) {
      console.error("Error updating user:", err.response?.data || err.message);
      alert(err.response?.data?.message || "Failed to update user.");
    }
  };

  // Delete a user by ID
  const deleteUser = async (userId) => {
    try {
      const token = localStorage.getItem("authToken");
      const response = await axios.delete(`http://localhost:5000/api/users/${userId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      alert(response.data.message || "User deleted successfully!");
      fetchUsers();
    } catch (err) {
      console.error("Error deleting user:", err.response?.data || err.message);
      alert(err.response?.data?.message || "Failed to delete user.");
    }
  };

  // Handle Logout
  const handleLogout = () => {
    localStorage.removeItem("authToken");
    window.location.href = "/login";
  };
  const handleFilterChange = (type) => {
    setFilterType(type);
    setFilterCategory('All'); // Reset category filter when changing the menu filter
  };

  const handleCategoryChange = (category) => {
    setFilterCategory(category);
    setFilterType('All'); // Reset menu filter when changing the category filter
  };
 // Filtered items based on the selected filterType and filterCategory
 const filteredItems = items.filter((item) => {
  const matchesType = filterType === 'All' || item.postType === filterType;
  const matchesCategory =
    filterCategory === 'All' || item.category === filterCategory;
  return matchesType && matchesCategory;
});
  return (
    <div className="admin-dashboard">
      {/* Navigation Bar */}
      <div className="left-sidebar">
  <h2 className="sidebar-title">BukSU Lost and Found</h2>
  <ul className="sidebar-menu">
    <li>
      <a href="/home" className="sidebar-link">
        <i className="fa-solid fa-house"></i> Home
      </a>
    </li>
    <li>
      <a href="/lost" className="sidebar-link">
        <i className="fa-solid fa-magnifying-glass"></i> Lost
      </a>
    </li>
    <li>
      <a href="/found" className="sidebar-link">
        <i className="fa-solid fa-box-open"></i> Found
      </a>
    </li>
    <li>
      <a href="/notifications" className="sidebar-link">
        <i className="fa-solid fa-bell"></i> Notifications
      </a>
    </li>
    <li>
      <a href="/profile" className="sidebar-link">
        <i className="fa-solid fa-user"></i> Profile
      </a>
    </li>
    <li>
      <a href="/settings" className="sidebar-link">
        <i className="fa-solid fa-gear"></i> Settings
      </a>
    </li>
  </ul>
  <div className="filter-category">
    <h3>Filter by Category</h3>
    <ul>
      <li>All Categories</li>
      <li>Electronics</li>
      <li>Personal Items</li>
      <li>ID</li>
      <li>Other</li>
    </ul>
  </div>
  <button className="post-btn">Post</button>
</div>


      {/* Main Content */}
      <div className="content">
        {/* Add User Form */}
        <div className="add-user-form">
          <h2>Add New User</h2>
          <input
            type="text"
            placeholder="First Name"
            value={newUser.firstname}
            onChange={(e) => setNewUser({ ...newUser, firstname: e.target.value })}
          />
          <input
            type="text"
            placeholder="Last Name"
            value={newUser.lastname}
            onChange={(e) => setNewUser({ ...newUser, lastname: e.target.value })}
          />
          <input
            type="email"
            placeholder="Email"
            value={newUser.email}
            onChange={(e) => setNewUser({ ...newUser, email: e.target.value })}
          />
          <input
            type="password"
            placeholder="Password"
            value={newUser.password}
            onChange={(e) => setNewUser({ ...newUser, password: e.target.value })}
          />
          <select
            value={newUser.userType}
            onChange={(e) => setNewUser({ ...newUser, userType: e.target.value })}
          >
            <option value="student">Student</option>
            <option value="staff">Staff</option>
            <option value="faculty">Faculty</option>
            <option value="admin">Admin</option>
          </select>
          <button onClick={addUser}>Add User</button>
        </div>

        {/* Registered Users Table */}
        <div className="registered-users">
          <h2>Registered Users</h2>
          {loading && <p>Loading users...</p>}
          {error && <p className="error-message">{error}</p>}
          {!loading && !error && users.length === 0 && <p>No registered users found.</p>}
          {!loading && users.length > 0 && (
            <div className="table-container">
              <table>
                <thead>
                  <tr>
                    <th>First Name</th>
                    <th>Last Name</th>
                    <th>Email</th>
                    <th>User Type</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {users.map((user) => (
                    <tr key={user._id}>
                      {editingUser && editingUser._id === user._id ? (
                        <>
                          <td><input type="text" value={editingUser.firstname} onChange={(e) => setEditingUser({ ...editingUser, firstname: e.target.value })} /></td>
                          <td><input type="text" value={editingUser.lastname} onChange={(e) => setEditingUser({ ...editingUser, lastname: e.target.value })} /></td>
                          <td><input type="email" value={editingUser.email} onChange={(e) => setEditingUser({ ...editingUser, email: e.target.value })} /></td>
                          <td>
                            <select value={editingUser.userType} onChange={(e) => setEditingUser({ ...editingUser, userType: e.target.value })}>
                              <option value="student">Student</option>
                              <option value="staff">Staff</option>
                              <option value="faculty">Faculty</option>
                              <option value="admin">Admin</option>
                            </select>
                          </td>
                          <td>
                            <button onClick={updateUser}><i className="fa-solid fa-save" style={{ color: "#28a745" }}></i></button>
                            <button onClick={() => setEditingUser(null)}><i className="fa-solid fa-times" style={{ color: "#dc3545" }}></i></button>
                          </td>
                        </>
                      ) : (
                        <>
                          <td>{user.firstname}</td>
                          <td>{user.lastname}</td>
                          <td>{user.email}</td>
                          <td>{user.userType}</td>
                          <td>
  <div className="action-buttons">
    {/* Edit button */}
    <button onClick={() => setEditingUser(user)}>
      <i className="fa-solid fa-pen-to-square"></i>
    </button>

    {/* Delete button */}
    <button onClick={() => deleteUser(user._id)}>
      <i className="fa-solid fa-trash"></i>
    </button>

    {/* Message button */}
    <a
      href={`https://mail.google.com/mail/?view=cm&fs=1&to=${user.email}&su=Message from Admin&body=Hello ${user.firstname},`}
      target="_blank"
      rel="noopener noreferrer"
    >
      <button>
        <i className="fa-solid fa-envelope"></i>
      </button>
    </a>
  </div>
</td>


                        </>
                      )}
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
