import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../styles/AdminDashboard.css';

const AdminDashboard = () => {
  const navigate = useNavigate();
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch users list from API
  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/users');
        setUsers(response.data);
      } catch (error) {
        console.error('Error fetching users:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchUsers();
  }, []);

  // Logout handler
  const handleLogout = () => {
    localStorage.removeItem('admin');
    navigate('/admin-login');
  };

  // Delete user handler
  const handleDeleteUser = async (userId) => {
    try {
      await axios.delete(`http://localhost:5000/api/users/${userId}`);
      setUsers(users.filter(user => user._id !== userId)); // Remove deleted user from state
    } catch (error) {
      console.error('Error deleting user:', error);
    }
  };

  // Edit user handler (navigate to edit page)
  const handleEditUser = (userId) => {
    navigate(`/admin-edit-user/${userId}`);
  };

  // Create new user handler (navigate to create page)
  const handleCreateUser = () => {
    navigate('/admin-create-user');
  };

  return (
    <div className="admin-dashboard">
      <div className="admin-navbar">
        <h2>Admin Dashboard</h2>
        <div className="admin-actions">
          <button onClick={handleLogout} className="logout-button">
            Logout
          </button>
        </div>
      </div>

      <div className="dashboard-content">
        <h3>Welcome, Admin!</h3>
        <p>This page is used to monitor users and perform CRUD operations.</p>

        <button onClick={handleCreateUser} className="create-user-button">
          Create New User
        </button>

        {loading ? (
          <p>Loading users...</p>
        ) : (
          <div className="users-list">
            <h4>Users List</h4>
            <table className="users-table">
              <thead>
                <tr>
                  <th>Email</th>
                  <th>User Type</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {users.map((user) => (
                  <tr key={user._id}>
                    <td>{user.email}</td>
                    <td>{user.userType}</td>
                    <td>
                      <button
                        onClick={() => handleEditUser(user._id)}
                        className="edit-button"
                      >
                        Edit
                      </button>
                      <button
                        onClick={() => handleDeleteUser(user._id)}
                        className="delete-button"
                      >
                        Delete
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminDashboard;
