import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/ProfilePage.css';

const ProfilePage = () => {
  const [userDetails, setUserDetails] = useState({});
  const [postedItems, setPostedItems] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchUserDetails();
    fetchPostedItems();
  }, []);

  const fetchUserDetails = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        throw new Error('No token found. Please log in again.');
      }

      const response = await axios.get('http://localhost:5000/api/profile', {
        headers: { Authorization: `Bearer ${token}` },
      });

      setUserDetails(response.data);
    } catch (err) {
      console.error('Error fetching user details:', err.response?.data || err.message);
      setError('Failed to load your profile details. Please log in again.');
    }
  };

  const fetchPostedItems = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        throw new Error('No token found. Please log in again.');
      }

      const response = await axios.get('http://localhost:5000/api/items/user', {
        headers: { Authorization: `Bearer ${token}` },
      });

      setPostedItems(response.data);
    } catch (err) {
      console.error('Error fetching posted items:', err.response?.data || err.message);
      setError('Failed to load your posted items. Please try again.');
    }
  };

  return (
    <div className="profile-page">
      <div className="profile-sidebar">
        <h2>Profile</h2>
        <p><strong>Name:</strong> {userDetails.fullname}</p>
        <p><strong>Email:</strong> {userDetails.email}</p>
        <p><strong>User Type:</strong> {userDetails.userType}</p>
      </div>
      <div className="profile-content">
        <h2>Your Posted Items</h2>
        {error && <p className="error">{error}</p>}
        <div className="posted-items">
          {postedItems.length === 0 ? (
            <p>No items have been posted yet.</p>
          ) : (
            postedItems.map((item) => (
              <div key={item._id} className="posted-item">
                <img
                  src={`http://localhost:5000/uploads/${item.image}`}
                  alt={item.description}
                  className="item-image"
                />
                <p><strong>Description:</strong> {item.description}</p>
                <p><strong>Category:</strong> {item.category}</p>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
};

export default ProfilePage;
