import React, { useState, useEffect } from "react";
import axios from "axios";
import "../styles/AdminDashboard.css";

const AdminDashboard = () => {
  const [currentView, setCurrentView] = useState("registeredUsers");
  const [users, setUsers] = useState([]);
  const [items, setItems] = useState([]);
  const [categoryFilter, setCategoryFilter] = useState("All");
  const [editingUser, setEditingUser] = useState(null); // Holds user being edited
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (currentView === "registeredUsers") fetchUsers();
    if (currentView === "lostItems" || currentView === "foundItems") fetchItems();
  }, [currentView, categoryFilter]);

  // Fetch Registered Users
  const fetchUsers = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem("authToken");
      const response = await axios.get("http://localhost:5000/api/users/all-users", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUsers(response.data);
    } catch (err) {
      setError("Failed to fetch users.");
    } finally {
      setLoading(false);
    }
  };

  // Fetch Lost/Found Items
  const fetchItems = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem("authToken");
      const response = await axios.get("http://localhost:5000/api/items", {
        headers: { Authorization: `Bearer ${token}` },
      });

      let filteredItems = response.data.filter(
        (item) =>
          (currentView === "lostItems" ? item.postType === "Lost" : item.postType === "Found") &&
          (categoryFilter === "All" || item.category === categoryFilter)
      );

      setItems(filteredItems);
    } catch (err) {
      setError("Failed to fetch items.");
    } finally {
      setLoading(false);
    }
  };

  // Update User
  const handleSaveEdit = async () => {
    try {
      const token = localStorage.getItem("authToken");
      await axios.put(
        `http://localhost:5000/api/users/${editingUser._id}`,
        editingUser,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert("User updated successfully!");
      setEditingUser(null);
      fetchUsers();
    } catch (err) {
      alert("Failed to update user.");
    }
  };

  // Delete User
  const handleDeleteUser = async (userId) => {
    try {
      const token = localStorage.getItem("authToken");
      await axios.delete(`http://localhost:5000/api/users/${userId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert("User deleted successfully!");
      fetchUsers();
    } catch (err) {
      alert("Failed to delete user.");
    }
  };

  // Logout Function
  const handleLogout = () => {
    localStorage.removeItem("authToken");
    window.location.href = "/login"; // Redirect to login page
  };

  // Category Filter Handler
  const handleCategoryChange = (category) => {
    setCategoryFilter(category);
  };

  const renderView = () => {
    switch (currentView) {
      case "registeredUsers":
        return (
          <div className="registered-users">
            <h2>Registered Users</h2>
            <table>
              <thead>
                <tr>
                  <th>First Name</th>
                  <th>Last Name</th>
                  <th>Email</th>
                  <th>User Type</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {users.map((user) => (
                  <tr key={user._id}>
                    {editingUser && editingUser._id === user._id ? (
                      <>
                        <td>
                          <input
                            type="text"
                            value={editingUser.firstname}
                            onChange={(e) =>
                              setEditingUser({ ...editingUser, firstname: e.target.value })
                            }
                          />
                        </td>
                        <td>
                          <input
                            type="text"
                            value={editingUser.lastname}
                            onChange={(e) =>
                              setEditingUser({ ...editingUser, lastname: e.target.value })
                            }
                          />
                        </td>
                        <td>
                          <input
                            type="email"
                            value={editingUser.email}
                            onChange={(e) =>
                              setEditingUser({ ...editingUser, email: e.target.value })
                            }
                          />
                        </td>
                        <td>
                          <select
                            value={editingUser.userType}
                            onChange={(e) =>
                              setEditingUser({ ...editingUser, userType: e.target.value })
                            }
                          >
                            <option value="student">Student</option>
                            <option value="staff">Staff</option>
                            <option value="faculty">Faculty</option>
                            <option value="admin">Admin</option>
                          </select>
                        </td>
                        <td>
                          <button onClick={handleSaveEdit}>💾 Save</button>
                          <button onClick={() => setEditingUser(null)}>❌ Cancel</button>
                        </td>
                      </>
                    ) : (
                      <>
                        <td>{user.firstname}</td>
                        <td>{user.lastname}</td>
                        <td>{user.email}</td>
                        <td>{user.userType}</td>
                        <td>
                          <button onClick={() => setEditingUser(user)}>✏️ Edit</button>
                          <button onClick={() => handleDeleteUser(user._id)}>🗑️ Delete</button>
                          <a
                            href={`https://mail.google.com/mail/?view=cm&fs=1&to=${user.email}&su=Message from Admin&body=Hello ${user.firstname},`}
                            target="_blank"
                            rel="noopener noreferrer"
                          >
                            ✉️ Message
                          </a>
                        </td>
                      </>
                    )}
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        );
      default:
        return <p>Select an option from the menu.</p>;
    }
  };

  return (
    <div className="admin-dashboard">
      <div className="left-sidebar">
        <h2>Admin Menu</h2>
        <ul>
          <li onClick={() => setCurrentView("registeredUsers")}>Registered Users</li>
          <li onClick={() => setCurrentView("lostItems")}>Lost Items</li>
          <li onClick={() => setCurrentView("foundItems")}>Found Items</li>
          <li onClick={handleLogout} style={{ color: "red", cursor: "pointer" }}>
            Logout
          </li>
        </ul>
      </div>
      <div className="content">{renderView()}</div>
    </div>
  );
};

export default AdminDashboard;
