import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { GoogleLogin } from '@react-oauth/google';  // Import the new package
import axios from 'axios';
import '../styles/Login.css';

const Login = () => {
  const [loginData, setLoginData] = useState({
    email: '',
    password: '',
  });
  
  const navigate = useNavigate();  // Initialize navigate to redirect

  const handleChange = (e) => {
    setLoginData({
      ...loginData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/auth/login', loginData); // Fixed route
      if (response.data.token) {
        localStorage.setItem('token', response.data.token);
        navigate('/user-home');
      } else {
        alert('Login failed: Invalid response from server.');
      }
    } catch (error) {
      console.error('Error:', error.response?.data || error.message);
      alert('Login failed: ' + (error.response?.data.message || error.message));
    }
  };
  

  const handleGoogleLoginSuccess = async (response) => {
    const googleToken = response.credential; // ID Token from Google
    try {
      const res = await axios.post('http://localhost:5000/api/auth/google', { token: googleToken }); // Updated route
      if (res.data.success) {
        localStorage.setItem('token', res.data.token);
        navigate('/user-home');
      } else {
        alert('Google login failed');
      }
    } catch (error) {
      console.error('Google login error:', error.response?.data || error.message);
      alert('Google login failed');
    }
  };
  return (
    <div className="login">
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Email</label>
          <input
            type="email"
            name="email"
            value={loginData.email}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Password</label>
          <input
            type="password"
            name="password"
            value={loginData.password}
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit" className="login-button">Login</button>
      </form>

      <div className="google-login">
        <GoogleLogin
          onSuccess={handleGoogleLoginSuccess}
          onError={() => console.log('Login Failed')}
        />
      </div>
    </div>
  );
};

export default Login;
