import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/LostAndFoundPage.css';

const LostPage = () => {
  const [lostItems, setLostItems] = useState([]);

  useEffect(() => {
    fetchLostItems();
  }, []);

  const fetchLostItems = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:5000/api/items', {
        headers: { Authorization: `Bearer ${token}` },
      });
      const filteredItems = response.data.filter((item) => item.postType === 'Lost');
      setLostItems(filteredItems);
    } catch (error) {
      console.error('Error fetching lost items:', error);
    }
  };

  return (
    <div className="lost-found-page">
      <h1>Lost Items</h1>
      <div className="items-container">
        {lostItems.length === 0 ? (
          <p className="no-items">No lost items available.</p>
        ) : (
          lostItems.map((item) => (
            <div key={item._id} className="item-card">
              {item.image && <img src={`http://localhost:5000/uploads/${item.image}`} alt={item.description} />}
              <div className="item-details">
                <p><strong>Description:</strong> {item.description}</p>
                <p><strong>Category:</strong> {item.category}</p>
                <p><strong>Post Type:</strong> {item.postType}</p>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default LostPage;
