// src/pages/ProfilePage.jsx
import React, { useEffect, useState } from 'react';
import axios from 'axios'; // Import axios for making HTTP requests

const ProfilePage = () => {
  const [user, setUser] = useState(null);  // Store user data
  const [loading, setLoading] = useState(true);  // State for loading

  useEffect(() => {
    // Fetch the user data from the backend API
    const fetchUserData = async () => {
      try {
        const response = await axios.get('/api/user/profile', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`, // Include token from localStorage
          },
        });
        setUser(response.data);  // Set user data
        setLoading(false);  // Set loading to false
      } catch (error) {
        console.error('Error fetching user data:', error);
        setLoading(false);
      }
    };

    fetchUserData();  // Call the function to fetch data
  }, []);

  if (loading) {
    return <div>Loading...</div>;  // Show loading state while fetching data
  }

  if (!user) {
    return <div>Error fetching user data</div>;  // Show error if no user data
  }

  return (
    <div className="profile-page">
      <h1>User Profile</h1>
      <div className="profile-container">
        {/* Left Side: User Info */}
        <div className="profile-sidebar">
          <img src="profile-icon.png" alt="Profile Icon" />
          <h2>{user.email}</h2>
          <p>User Type: {user.userType}</p>

          <button>Post an Item</button>
          <button>Update Account</button>
          <button>Log Out</button>
          <button>Delete Account</button>
        </div>

        {/* Right Side: Display Posted Items */}
        <div className="posted-items">
          <h3>Posted Items</h3>
          {/* Display posted items here */}
          {/* For now, show dummy data */}
          <ul>
            <li>Item 1: Laptop</li>
            <li>Item 2: Umbrella</li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default ProfilePage;
