import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AdminDashboard = () => {
  const [users, setUsers] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        // Retrieve token from localStorage
        const token = localStorage.getItem('token');

        if (!token) {
          setError('No token found. Please log in again.');
          return;
        }

        // Make the request with Authorization header containing the token
        const response = await axios.get('http://localhost:5000/api/users/all-users', {
          headers: { Authorization: `Bearer ${token}` },
        });

        setUsers(response.data); // Save users to state
      } catch (err) {
        console.error('Error fetching users:', err.response?.data || err.message);
        setError(err.response?.data?.message || 'Error fetching users');
      }
    };

    fetchUsers();
  }, []);

  return (
    <div style={{ padding: '20px' }}>
      <h1>Admin Dashboard</h1>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {!error && (
        <table border="1" cellPadding="10" style={{ margin: '20px 0', borderCollapse: 'collapse' }}>
          <thead>
            <tr>
              <th>Email</th>
              <th>User Type</th>
            </tr>
          </thead>
          <tbody>
            {users.map((user) => (
              <tr key={user._id}>
                <td>{user.email}</td>
                <td>{user.userType}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default AdminDashboard;
