import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import "../styles/AdminDashboard.css";

const AdminDashboard = () => {
  const [activeSection, setActiveSection] = useState("addUser");
  const [users, setUsers] = useState([]);
  const [items, setItems] = useState([]);
  const [newUser, setNewUser] = useState({
    firstname: "",
    lastname: "",
    email: "",
    password: "",
    userType: "student",
  });

  const [editingUser, setEditingUser] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (activeSection === "registeredUsers") fetchUsers();
    if (activeSection === "lostAndFoundItems") fetchItems();
  }, [activeSection]);

  const fetchUsers = async () => {
    setLoading(true);
    try {
      const token = localStorage.getItem("authToken");
      const response = await axios.get("http://localhost:5000/api/users/all-users", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUsers(response.data);
    } catch (err) {
      console.error("Error fetching users:", err);
    } finally {
      setLoading(false);
    }
  };

  const fetchItems = async () => {
    setLoading(true);
    try {
      const token = localStorage.getItem("authToken");
      const response = await axios.get("http://localhost:5000/api/items", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setItems(response.data);
    } catch (err) {
      console.error("Error fetching items:", err);
    } finally {
      setLoading(false);
    }
  };

  const addUser = async () => {
    try {
      const token = localStorage.getItem("authToken");
      await axios.post("http://localhost:5000/api/auth/register", newUser, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert("User added successfully");
      setNewUser({ firstname: "", lastname: "", email: "", password: "", userType: "student" });
      fetchUsers();
    } catch (err) {
      console.error("Error adding user:", err);
      alert("Failed to add user");
    }
  };

  const editUser = async (user) => {
    try {
      const token = localStorage.getItem("authToken");
      await axios.put(`http://localhost:5000/api/users/${user._id}`, user, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert("User updated successfully");
      setEditingUser(null);
      fetchUsers();
    } catch (err) {
      console.error("Error updating user:", err);
    }
  };

  const deleteUser = async (userId) => {
    try {
      const token = localStorage.getItem("authToken");
      await axios.delete(`http://localhost:5000/api/users/${userId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert("User deleted successfully");
      fetchUsers();
    } catch (err) {
      console.error("Error deleting user:", err);
    }
  };

  const renderAddUser = () => (
    <div className="add-user-form">
      <h2>Add New User</h2>
      <input
        type="text"
        placeholder="First Name"
        value={newUser.firstname}
        onChange={(e) => setNewUser({ ...newUser, firstname: e.target.value })}
      />
      <input
        type="text"
        placeholder="Last Name"
        value={newUser.lastname}
        onChange={(e) => setNewUser({ ...newUser, lastname: e.target.value })}
      />
      <input
        type="email"
        placeholder="Email"
        value={newUser.email}
        onChange={(e) => setNewUser({ ...newUser, email: e.target.value })}
      />
      <input
        type="password"
        placeholder="Password"
        value={newUser.password}
        onChange={(e) => setNewUser({ ...newUser, password: e.target.value })}
      />
      <select
        value={newUser.userType}
        onChange={(e) => setNewUser({ ...newUser, userType: e.target.value })}
      >
        <option value="student">Student</option>
        <option value="staff">Staff</option>
        <option value="faculty">Faculty</option>
        <option value="admin">Admin</option>
      </select>
      <button onClick={addUser}>Add User</button>
    </div>
  );

  const renderRegisteredUsers = () => (
    <div className="registered-users">
      <h2>Registered Users</h2>
      {loading && <p>Loading users...</p>}
      {users.map((user) => (
        <div key={user._id} className="user-item">
          <p>
            {user.firstname} {user.lastname} - {user.email} ({user.userType})
          </p>
          <button onClick={() => deleteUser(user._id)}>Delete</button>
        </div>
      ))}
    </div>
  );

  const renderLostAndFoundItems = () => (
    <div className="lost-found-items">
      <h2>Lost and Found Items</h2>
      {loading && <p>Loading items...</p>}
      <div className="items-feed">
        {items.map((item) => (
          <div className="item-post" key={item._id}>
            <div className="post-header">
              <strong>
                {item.postedBy ? `${item.postedBy.firstname} ${item.postedBy.lastname}` : "Unknown"}
              </strong>
              <span> â€¢ {new Date(item.createdAt).toLocaleDateString()}</span>
            </div>
            <div className="post-body">
              {item.image && <img src={`http://localhost:5000/uploads/${item.image}`} alt={item.description} />}
              <p>{item.description}</p>
              <p>Category: {item.category}</p>
              <p>Type: {item.postType}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );

  return (
    <div className="admin-dashboard">
      <div className="sidebar">
        <h2>Admin Menu</h2>
        <ul>
          <li onClick={() => setActiveSection("addUser")}>Add User</li>
          <li onClick={() => setActiveSection("registeredUsers")}>Registered Users</li>
          <li onClick={() => setActiveSection("lostAndFoundItems")}>Lost and Found Items</li>
        </ul>
      </div>

      <div className="content">
        {activeSection === "addUser" && renderAddUser()}
        {activeSection === "registeredUsers" && renderRegisteredUsers()}
        {activeSection === "lostAndFoundItems" && renderLostAndFoundItems()}
      </div>
    </div>
  );
};

export default AdminDashboard;
