// pages/Login.jsx
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../styles/Login.css';

const Login = () => {
  const [loginData, setLoginData] = useState({
    email: '',
    password: '',
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    setLoginData({
      ...loginData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/login', loginData);
      console.log('Response:', response.data);
      if (response.data.success) {
        localStorage.setItem('token', response.data.token);
        navigate('/user-home');
      } else {
        alert('Login failed');
      }
    } catch (error) {
      console.error('Error:', error.response?.data || error.message);
      alert('Login failed');
    }
  };

  const handleGoogleSuccess = async (response) => {
    try {
      const googleToken = response.tokenId; // Get the Google token
      const res = await axios.post('http://localhost:5000/api/auth/google', { token: googleToken });
      console.log('Google login response:', res.data);
      if (res.data.success) {
        localStorage.setItem('token', res.data.token);
        navigate('/user-home');
      } else {
        alert('Google login failed');
      }
    } catch (error) {
      console.error('Google login error:', error.response?.data || error.message);
      alert('Google login failed');
    }
  };

  const handleGoogleFailure = (error) => {
    console.error('Google login failed:', error);
    alert('Google login failed. Please try again.');
  };

  return (
    <div className="login">
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Email</label>
          <input
            type="email"
            name="email"
            value={loginData.email}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Password</label>
          <input
            type="password"
            name="password"
            value={loginData.password}
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit" className="login-button">Login</button>
      </form>

      <div className="google-login">
        <h3>Or</h3>
        <GoogleLogin
          clientId={process.env.REACT_APP_GOOGLE_CLIENT_ID} // Use your Google Client ID
          buttonText="Continue with Google"
          onSuccess={handleGoogleSuccess}
          onFailure={handleGoogleFailure}
          cookiePolicy={'single_host_origin'}
        />
      </div>
    </div>
  );
};

export default Login;
