import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { GoogleLogin } from '@react-oauth/google';
import axios from 'axios';
import '../styles/Login.css';

const Login = () => {
  const [loginData, setLoginData] = useState({
    email: '',
    password: '',
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    setLoginData({
      ...loginData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const { email, password } = loginData;

    try {
      const endpoint = 'http://localhost:5000/api/auth/login';
      const res = await axios.post(endpoint, { email, password });
      const { token, userType } = res.data;

      if (token) {
        localStorage.setItem('token', token);
        userType === 'admin' ? navigate('/admin') : navigate('/user-home');
      } else {
        alert('Login failed: Invalid response from server.');
      }
    } catch (error) {
      console.error('Error:', error.response?.data || error.message);
      alert('Login failed: ' + (error.response?.data?.message || error.message));
    }
  };

  const handleGoogleLoginSuccess = async (response) => {
    const googleToken = response.credential;

    try {
      const res = await axios.post('http://localhost:5000/api/auth/google', {
        token: googleToken,
      });

      const { success, token, userType } = res.data;

      if (success) {
        localStorage.setItem('token', token);
        userType === 'admin' ? navigate('/admin') : navigate('/user-home');
      } else {
        alert('Google login failed.');
      }
    } catch (error) {
      console.error('Google login error:', error.response?.data || error.message);
      alert('Google login failed.');
    }
  };

  return (
    <div className="login-container">
      {/* Left Side Panel */}
      <div className="login-left">
        <h2 className="logo">Your Company</h2>
        <p className="welcome-text">
          Welcome to the portal. Please log in to access your account and enjoy seamless services.
        </p>
      </div>

      {/* Right Side Panel */}
      <div className="login-right">
        <div className="login-form">
          <h2>Login</h2>
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <input
                type="email"
                name="email"
                placeholder="Email"
                value={loginData.email}
                onChange={handleChange}
                required
              />
            </div>
            <div className="form-group">
              <input
                type="password"
                name="password"
                placeholder="Password"
                value={loginData.password}
                onChange={handleChange}
                required
              />
            </div>
            <button type="submit" className="login-button">
              Login
            </button>
          </form>
          <div className="divider">OR</div>
          <div className="google-login">
            <GoogleLogin
              onSuccess={handleGoogleLoginSuccess}
              onError={() => console.log('Login Failed')}
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
