// src/components/UserNavbar.jsx

import React from 'react';
import { useNavigate } from 'react-router-dom';  // Import useNavigate
import '..styles/UserNavbar.css'; // Ensure your CSS file is imported

const UserNavbar = () => {
  const navigate = useNavigate();  // Initialize the navigate function

  const handleLogout = () => {
    // Clear the token from localStorage
    localStorage.removeItem('token');
    
    // Use navigate to redirect to the login page
    navigate('/login');  // This will navigate to /login without reloading the page
  };

  return (
    <nav className="user-navbar">
      <div className="navbar-logo">
        <h1>BukSU Lost and Found</h1>
      </div>
      <div className="navbar-links">
        <ul>
          <li><a href="/home">Home</a></li>
          <li><a href="/category">Category</a></li>
          <li><a href="/about">About Us</a></li>
          <li><a href="/contact">Contact Us</a></li>
          <li><a href="/profile"><img src="user-icon.png" alt="Profile" /></a></li>
          <li><button onClick={handleLogout}>Logout</button></li>
        </ul>
      </div>
    </nav>
  );
};

export default UserNavbar;
