import React from 'react';
import { GoogleLogin } from '@react-oauth/google';
import { useNavigate } from 'react-router-dom'; // for redirecting after successful login
import axios from 'axios';
import '../styles/Register.css';

function Register() {
  const navigate = useNavigate();

  const handleGoogleLogin = async (response) => {
    try {
      const { credential } = response;
      
      // Send the Google token to your backend
      const { data } = await axios.post('/api/auth/google-login', {
        token: credential,
      });

      // Store JWT token (or you could also use session cookies)
      localStorage.setItem('token', data.token);
      
      // Redirect to the user home page or dashboard after successful login
      navigate('/user-home');
    } catch (error) {
      console.error("Google Login Error: ", error);
    }
  };

  return (
    <div className="register">
      <h2>Register</h2>
      <form>
        {/* Regular registration form fields */}
        <input type="text" placeholder="First Name" />
        <input type="text" placeholder="Last Name" />
        <input type="email" placeholder="Email" />
        <input type="password" placeholder="Password" />
        <button type="submit">Register</button>
      </form>

      {/* Google Login Button */}
      <div className="google-login">
        <GoogleLogin 
          onSuccess={handleGoogleLogin} 
          onError={() => console.log('Google Login Failed')} 
        />
      </div>
    </div>
  );
}

export default Register;
