import React, { useState, useEffect } from "react";
import axios from "axios";
import "../styles/AdminDashboard.css";

const AdminDashboard = () => {
  const [users, setUsers] = useState([]);
  const [items, setItems] = useState([]); // Store Lost and Found items
  const [newUser, setNewUser] = useState({
    firstname: "",
    lastname: "",
    email: "",
    password: "",
    userType: "student",
  });
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchUsers();
    fetchItems(); // Fetch items on component load
  }, []);

  // Fetch all registered users
  const fetchUsers = async () => {
    setLoading(true);
    setError(null);
    try {
      const token = localStorage.getItem("authToken");
      if (!token) {
        throw new Error("No token found. Please log in again.");
      }

      const response = await axios.get("http://localhost:5000/api/users/all-users", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUsers(response.data);
    } catch (err) {
      console.error("Error fetching users:", err.response?.data || err.message);
      setError(err.response?.data?.message || "Failed to fetch users.");
    } finally {
      setLoading(false);
    }
  };

  // Fetch all Lost and Found items
  const fetchItems = async () => {
    try {
      const token = localStorage.getItem("authToken");
      if (!token) {
        throw new Error("No token found. Please log in again.");
      }

      const response = await axios.get("http://localhost:5000/api/items", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setItems(response.data);
    } catch (err) {
      console.error("Error fetching items:", err.response?.data || err.message);
      setError(err.response?.data?.message || "Failed to fetch items.");
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("authToken");
    window.location.href = "/login"; // Redirect to login page
  };

  return (
    <div className="admin-dashboard">
      <nav className="navbar">
        <div className="navbar-brand">Admin Dashboard</div>
        <ul className="navbar-links">
          <li>
            <button className="logout-btn" onClick={handleLogout}>
              Logout
            </button>
          </li>
        </ul>
      </nav>

      <div className="content">
        {/* Add New User Section */}
        <div className="add-user-form">
          <h2>Add New User</h2>
          <input
            type="text"
            placeholder="First Name"
            value={newUser.firstname}
            onChange={(e) => setNewUser({ ...newUser, firstname: e.target.value })}
          />
          <input
            type="text"
            placeholder="Last Name"
            value={newUser.lastname}
            onChange={(e) => setNewUser({ ...newUser, lastname: e.target.value })}
          />
          <input
            type="email"
            placeholder="Email"
            value={newUser.email}
            onChange={(e) => setNewUser({ ...newUser, email: e.target.value })}
          />
          <input
            type="password"
            placeholder="Password"
            value={newUser.password}
            onChange={(e) => setNewUser({ ...newUser, password: e.target.value })}
          />
          <select
            value={newUser.userType}
            onChange={(e) => setNewUser({ ...newUser, userType: e.target.value })}
          >
            <option value="student">Student</option>
            <option value="staff">Staff</option>
            <option value="faculty">Faculty</option>
            <option value="admin">Admin</option>
          </select>
          <button>Add User</button>
        </div>

        {/* Registered Users Section */}
        <div className="registered-users">
          <h2>Registered Users</h2>
          {loading && <p>Loading users...</p>}
          {error && <p className="error-message">{error}</p>}
          {!loading && !error && users.length === 0 && <p>No registered users found.</p>}
          {!loading && users.length > 0 && (
            <div className="table-container">
              <table>
                <thead>
                  <tr>
                    <th>First Name</th>
                    <th>Last Name</th>
                    <th>Email</th>
                    <th>User Type</th>
                  </tr>
                </thead>
                <tbody>
                  {users.map((user) => (
                    <tr key={user._id}>
                      <td>{user.firstname}</td>
                      <td>{user.lastname}</td>
                      <td>{user.email}</td>
                      <td>{user.userType}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>

        {/* Lost and Found Items Section */}
        <div className="lost-found-items">
          <h2>Lost and Found Items</h2>
          {items.length === 0 ? (
            <p>No items found.</p>
          ) : (
            <div className="table-container">
              <table>
                <thead>
                  <tr>
                    <th>Post Type</th>
                    <th>Category</th>
                    <th>Description</th>
                    <th>Posted By</th>
                  </tr>
                </thead>
                <tbody>
                  {items.map((item) => (
                    <tr key={item._id}>
                      <td>{item.postType}</td>
                      <td>{item.category}</td>
                      <td>{item.description}</td>
                      <td>
                        {item.postedBy
                          ? `${item.postedBy.firstname} ${item.postedBy.lastname}`
                          : "Unknown"}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
