import React, { useState, useEffect } from "react";
import axios from "axios";
import "../styles/AdminDashboard.css";

const AdminDashboard = () => {
  const [users, setUsers] = useState([]);
  const [newUser, setNewUser] = useState({
    firstname: "",
    lastname: "",
    email: "",
    password: "",
    userType: "student",
  });
  const [editingUser, setEditingUser] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [currentView, setCurrentView] = useState("addUser");

  useEffect(() => {
    fetchUsers();
  }, []);

  // Fetch all registered users
  const fetchUsers = async () => {
    setLoading(true);
    setError(null);
    try {
      const token = localStorage.getItem("authToken");
      const response = await axios.get("http://localhost:5000/api/users/all-users", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUsers(response.data);
    } catch (err) {
      setError("Failed to fetch users.");
    } finally {
      setLoading(false);
    }
  };

  // Add new user
  const addUser = async () => {
    try {
      const token = localStorage.getItem("authToken");
      await axios.post("http://localhost:5000/api/auth/register", newUser, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert("User added successfully!");
      setNewUser({ firstname: "", lastname: "", email: "", password: "", userType: "student" });
      fetchUsers();
    } catch (err) {
      alert("Failed to add user.");
    }
  };

  // Delete user
  const deleteUser = async (userId) => {
    try {
      const token = localStorage.getItem("authToken");
      await axios.delete(`http://localhost:5000/api/users/${userId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchUsers();
    } catch (err) {
      alert("Failed to delete user.");
    }
  };

  // Save updated user
  const updateUser = async () => {
    try {
      const token = localStorage.getItem("authToken");
      await axios.put(`http://localhost:5000/api/users/${editingUser._id}`, editingUser, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert("User updated successfully!");
      setEditingUser(null);
      fetchUsers();
    } catch (err) {
      alert("Failed to update user.");
    }
  };

  const handleEditClick = (user) => {
    setEditingUser({ ...user }); // Initialize with user details for editing
  };
  const handleLogout = () => {
    localStorage.removeItem("authToken"); // Remove the auth token
    window.location.href = "/login"; // Redirect to login page
  };
  

  const renderView = () => {
    if (currentView === "addUser") {
      return (
        <div className="add-user-form">
          <h2>Add New User</h2>
          <input
            type="text"
            placeholder="First Name"
            value={newUser.firstname}
            onChange={(e) => setNewUser({ ...newUser, firstname: e.target.value })}
          />
          <input
            type="text"
            placeholder="Last Name"
            value={newUser.lastname}
            onChange={(e) => setNewUser({ ...newUser, lastname: e.target.value })}
          />
          <input
            type="email"
            placeholder="Email"
            value={newUser.email}
            onChange={(e) => setNewUser({ ...newUser, email: e.target.value })}
          />
          <input
            type="password"
            placeholder="Password"
            value={newUser.password}
            onChange={(e) => setNewUser({ ...newUser, password: e.target.value })}
          />
          <select
            value={newUser.userType}
            onChange={(e) => setNewUser({ ...newUser, userType: e.target.value })}
          >
            <option value="student">Student</option>
            <option value="staff">Staff</option>
            <option value="faculty">Faculty</option>
            <option value="admin">Admin</option>
          </select>
          <button onClick={addUser}>Add User</button>
        </div>
      );
    }

    if (currentView === "registeredUsers") {
      return (
        <div className="registered-users">
          <h2>Registered Users</h2>
          {loading ? <p>Loading...</p> : null}
          {error ? <p>{error}</p> : null}
          <table>
            <thead>
              <tr>
                <th>First Name</th>
                <th>Last Name</th>
                <th>Email</th>
                <th>User Type</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {users.map((user) => (
                <tr key={user._id}>
                  {editingUser && editingUser._id === user._id ? (
                    <>
                      <td>
                        <input
                          type="text"
                          value={editingUser.firstname}
                          onChange={(e) => setEditingUser({ ...editingUser, firstname: e.target.value })}
                        />
                      </td>
                      <td>
                        <input
                          type="text"
                          value={editingUser.lastname}
                          onChange={(e) => setEditingUser({ ...editingUser, lastname: e.target.value })}
                        />
                      </td>
                      <td>
                        <input
                          type="email"
                          value={editingUser.email}
                          onChange={(e) => setEditingUser({ ...editingUser, email: e.target.value })}
                        />
                      </td>
                      <td>
                        <select
                          value={editingUser.userType}
                          onChange={(e) => setEditingUser({ ...editingUser, userType: e.target.value })}
                        >
                          <option value="student">Student</option>
                          <option value="staff">Staff</option>
                          <option value="faculty">Faculty</option>
                          <option value="admin">Admin</option>
                        </select>
                      </td>
                      <td>
                        <button onClick={updateUser}>
                          <i className="fa-solid fa-save" title="Save"></i>
                        </button>
                        <button onClick={() => setEditingUser(null)}>
                          <i className="fa-solid fa-times" title="Cancel"></i>
                        </button>
                      </td>
                    </>
                  ) : (
                    <>
                      <td>{user.firstname}</td>
                      <td>{user.lastname}</td>
                      <td>{user.email}</td>
                      <td>{user.userType}</td>
     <div className="left-sidebar">
  <h2 className="sidebar-title">BukSU Lost and Found</h2>
  <ul className="sidebar-menu">
    <li>
      <a href="/add-user" className="sidebar-link">
        <i className="fa-solid fa-user-plus"></i> Add User
      </a>
    </li>
    <li>
      <a href="/registered-users" className="sidebar-link">
        <i className="fa-solid fa-users"></i> Registered Users
      </a>
    </li>
    <li>
      <a href="/lost-items" className="sidebar-link">
        <i className="fa-solid fa-magnifying-glass"></i> Lost Items
      </a>
    </li>
    <li>
      <a href="/found-items" className="sidebar-link">
        <i className="fa-solid fa-box-open"></i> Found Items
      </a>
    </li>
    <li>
      <a href="/categories" className="sidebar-link">
        <i className="fa-solid fa-layer-group"></i> Category
      </a>
    </li>
    <li>
      <a href="/logout" className="sidebar-link" onClick={handleLogout}>
        <i className="fa-solid fa-right-from-bracket"></i> Logout
      </a>
    </li>
  </ul>
</div>


                    </>
                  )}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      );
    }
  };

  return (
    <div className="admin-dashboard">
      <div className="left-sidebar">
        <h2>BukSU Lost and Found</h2>
        <ul>
          <li onClick={() => setCurrentView("addUser")}>Add User</li>
          <li onClick={() => setCurrentView("registeredUsers")}>Registered Users</li>
        </ul>
      </div>
      <div className="content">{renderView()}</div>
    </div>
  );
};

export default AdminDashboard;
