import React, { useState, useEffect } from "react";
import axios from "axios";
import "../styles/AdminDashboard.css";

const AdminDashboard = () => {
  const [users, setUsers] = useState([]);
  const [newUser, setNewUser] = useState({
    firstname: "",
    lastname: "",
    email: "",
    password: "",
    userType: "student",
  });
  const [editingUser, setEditingUser] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [currentView, setCurrentView] = useState("addUser");

  useEffect(() => {
    fetchUsers();
  }, []);

  // Fetch all registered users
  const fetchUsers = async () => {
    setLoading(true);
    setError(null);
    try {
      const token = localStorage.getItem("authToken");
      const response = await axios.get("http://localhost:5000/api/users/all-users", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUsers(response.data);
    } catch (err) {
      console.error("Error fetching users:", err.message);
      setError("Failed to fetch users.");
    } finally {
      setLoading(false);
    }
  };

  // Add new user
  const addUser = async () => {
    try {
      const token = localStorage.getItem("authToken");
      await axios.post("http://localhost:5000/api/auth/register", newUser, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert("User added successfully!");
      setNewUser({ firstname: "", lastname: "", email: "", password: "", userType: "student" });
      fetchUsers();
    } catch (err) {
      console.error("Error adding user:", err.message);
    }
  };

  // Delete user
  const deleteUser = async (userId) => {
    try {
      const token = localStorage.getItem("authToken");
      await axios.delete(`http://localhost:5000/api/users/${userId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert("User deleted successfully!");
      fetchUsers();
    } catch (err) {
      console.error("Error deleting user:", err.message);
    }
  };

  // Update user
  const updateUser = async () => {
    try {
      const token = localStorage.getItem("authToken");
      await axios.put(`http://localhost:5000/api/users/${editingUser._id}`, editingUser, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert("User updated successfully!");
      setEditingUser(null);
      fetchUsers();
    } catch (err) {
      console.error("Error updating user:", err.message);
    }
  };

  const renderView = () => {
    switch (currentView) {
      case "addUser":
        return (
          <div className="add-user-form">
            <h2>Add New User</h2>
            <input
              type="text"
              placeholder="First Name"
              value={newUser.firstname}
              onChange={(e) => setNewUser({ ...newUser, firstname: e.target.value })}
            />
            <input
              type="text"
              placeholder="Last Name"
              value={newUser.lastname}
              onChange={(e) => setNewUser({ ...newUser, lastname: e.target.value })}
            />
            <input
              type="email"
              placeholder="Email"
              value={newUser.email}
              onChange={(e) => setNewUser({ ...newUser, email: e.target.value })}
            />
            <input
              type="password"
              placeholder="Password"
              value={newUser.password}
              onChange={(e) => setNewUser({ ...newUser, password: e.target.value })}
            />
            <select
              value={newUser.userType}
              onChange={(e) => setNewUser({ ...newUser, userType: e.target.value })}
            >
              <option value="student">Student</option>
              <option value="staff">Staff</option>
              <option value="faculty">Faculty</option>
              <option value="admin">Admin</option>
            </select>
            <button onClick={addUser}>Add User</button>
          </div>
        );

      case "registeredUsers":
        return (
          <div className="registered-users">
            <h2>Registered Users</h2>
            {loading && <p>Loading users...</p>}
            {error && <p className="error-message">{error}</p>}
            <div className="table-container">
              <table>
                <thead>
                  <tr>
                    <th>First Name</th>
                    <th>Last Name</th>
                    <th>Email</th>
                    <th>User Type</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {users.map((user) => (
                    <tr key={user._id}>
                      <td>{user.firstname}</td>
                      <td>{user.lastname}</td>
                      <td>{user.email}</td>
                      <td>{user.userType}</td>
                      <td className="action-buttons">
                        <button onClick={() => setEditingUser(user)}>
                          <i className="fa-solid fa-pen-to-square" title="Edit"></i>
                        </button>
                        <button onClick={() => deleteUser(user._id)}>
                          <i className="fa-solid fa-trash" title="Delete"></i>
                        </button>
                        <a
                          href={`https://mail.google.com/mail/?view=cm&fs=1&to=${user.email}&su=Message from Admin&body=Hello ${user.firstname},`}
                          target="_blank"
                          rel="noopener noreferrer"
                        >
                          <button>
                            <i className="fa-solid fa-envelope" title="Message"></i>
                          </button>
                        </a>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        );

      default:
        return <h2>Welcome to the Admin Dashboard</h2>;
    }
  };

  return (
    <div className="admin-dashboard">
      {/* Sidebar */}
      <div className="left-sidebar">
        <h2 className="sidebar-title">BukSU Lost and Found</h2>
        <ul className="sidebar-menu">
          <li onClick={() => setCurrentView("addUser")}>
            <i className="fa-solid fa-user-plus"></i> Add User
          </li>
          <li onClick={() => setCurrentView("registeredUsers")}>
            <i className="fa-solid fa-users"></i> Registered Users
          </li>
        </ul>
      </div>

      {/* Main Content */}
      <div className="content">{renderView()}</div>
    </div>
  );
};

export default AdminDashboard;
