import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/AdminDashboard.css';

const AdminDashboard = () => {
  const [users, setUsers] = useState([]);
  const [newUser, setNewUser] = useState({ email: '', password: '', userType: 'student' });
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      const token = localStorage.getItem('authToken');
      const response = await axios.get('http://localhost:5000/api/users/all-users', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUsers(response.data);
    } catch (err) {
      console.error('Error fetching users:', err.response?.data || err.message);
      setError('Error fetching users.');
    }
  };

  const addUser = async () => {
    try {
      const token = localStorage.getItem('authToken');
      if (!newUser.email || !newUser.password || !newUser.userType) {
        alert('All fields are required.');
        return;
      }

      const response = await axios.post(
        'http://localhost:5000/api/auth/register',
        newUser,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      alert(response.data.message);
      setNewUser({ email: '', password: '', userType: 'student' });
      fetchUsers(); // Refresh user list
    } catch (err) {
      console.error('Error adding user:', err.response?.data || err.message);
      alert(err.response?.data?.message || 'Failed to add user.');
    }
  };

  return (
    <div>
      <h1>Admin Dashboard</h1>
      <div>
        <h2>Add New User</h2>
        <input
          type="email"
          placeholder="Email"
          value={newUser.email}
          onChange={(e) => setNewUser({ ...newUser, email: e.target.value })}
        />
        <input
          type="password"
          placeholder="Password"
          value={newUser.password}
          onChange={(e) => setNewUser({ ...newUser, password: e.target.value })}
        />
        <select
          value={newUser.userType}
          onChange={(e) => setNewUser({ ...newUser, userType: e.target.value })}
        >
          <option value="student">Student</option>
          <option value="staff">Staff</option>
          <option value="faculty">Faculty</option>
          <option value="admin">Admin</option>
        </select>
        <button onClick={addUser}>Add User</button>
      </div>

      <div>
        <h2>Registered Users</h2>
        {error && <p>{error}</p>}
        <table>
          <thead>
            <tr>
              <th>Email</th>
              <th>User Type</th>
            </tr>
          </thead>
          <tbody>
            {users.map((user) => (
              <tr key={user._id}>
                <td>{user.email}</td>
                <td>{user.userType}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default AdminDashboard;
