import React, { useState, useEffect } from "react";
import axios from "axios";
import "../styles/AdminDashboard.css";

const AdminDashboard = () => {
  const [currentView, setCurrentView] = useState("registeredUsers");
  const [users, setUsers] = useState([]);
  const [items, setItems] = useState([]);
  const [categoryFilter, setCategoryFilter] = useState("All");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (currentView === "registeredUsers") fetchUsers();
    if (currentView === "lostItems" || currentView === "foundItems") fetchItems();
  }, [currentView, categoryFilter]);

  // Fetch Registered Users
  const fetchUsers = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem("authToken");
      const response = await axios.get("http://localhost:5000/api/users/all-users", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUsers(response.data);
    } catch (err) {
      setError("Failed to fetch users.");
    } finally {
      setLoading(false);
    }
  };

  // Fetch Items with Category Filter
  const fetchItems = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem("authToken");
      const response = await axios.get("http://localhost:5000/api/items", {
        headers: { Authorization: `Bearer ${token}` },
      });

      let filteredItems = response.data.filter(
        (item) =>
          (currentView === "lostItems" ? item.postType === "Lost" : item.postType === "Found") &&
          (categoryFilter === "All" || item.category === categoryFilter)
      );

      setItems(filteredItems);
    } catch (err) {
      setError("Failed to fetch items.");
    } finally {
      setLoading(false);
    }
  };

  // Logout Function
  const handleLogout = () => {
    localStorage.removeItem("authToken");
    window.location.href = "/login"; // Redirect to login page
  };

  // Category Filter Handler
  const handleCategoryChange = (category) => {
    setCategoryFilter(category);
  };

  const renderView = () => {
    switch (currentView) {
      case "lostItems":
      case "foundItems":
        return (
          <div>
            <h2>{currentView === "lostItems" ? "Lost Items" : "Found Items"}</h2>
            <div className="filter-section">
              <label>Filter by Category:</label>
              <select onChange={(e) => handleCategoryChange(e.target.value)} value={categoryFilter}>
                <option value="All">All Categories</option>
                <option value="Electronics">Electronics</option>
                <option value="Personal Items">Personal Items</option>
                <option value="ID">ID</option>
                <option value="Other">Other</option>
              </select>
            </div>
            <div className="items-display">
              {items.length === 0 ? (
                <p>No items found.</p>
              ) : (
                items.map((item) => (
                  <div key={item._id} className="item-card">
                    {item.image && (
                      <img
                        src={`http://localhost:5000/uploads/${item.image}`}
                        alt={item.description}
                        className="item-image"
                      />
                    )}
                    <h4 className="item-category"><strong>Item Category:</strong>{" "}{item.category}</h4>
                    <p><strong>Description:</strong>{" "}{item.description}</p>
                    <p>
                      <strong>Posted By:</strong>{" "}
                      {item.postedBy
                        ? `${item.postedBy.firstname} ${item.postedBy.lastname}`
                        : "Unknown"}
                    </p>
                  </div>
                ))
              )}
            </div>
          </div>
        );

      case "registeredUsers":
        return (
          <div className="registered-users">
            <h2>Registered Users</h2>
            <table>
              <thead>
                <tr>
                  <th>First Name</th>
                  <th>Last Name</th>
                  <th>Email</th>
                  <th>User Type</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {users.map((user) => (
                  <tr key={user._id}>
                    <td>{user.firstname}</td>
                    <td>{user.lastname}</td>
                    <td>{user.email}</td>
                    <td>{user.userType}</td>
                    <td>
                      <button title="Edit">‚úèÔ∏è</button>
                      <button title="Delete">üóëÔ∏è</button>
                      <a
                        href={`https://mail.google.com/mail/?view=cm&fs=1&to=${user.email}&su=Message from Admin&body=Hello ${user.firstname},`}
                        target="_blank"
                        rel="noopener noreferrer"
                        title="Send Email"
                      >
                        ‚úâÔ∏è
                      </a>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        );
      default:
        return <p>Select an option from the menu.</p>;
    }
  };

  return (
    <div className="admin-dashboard">
      <div className="left-sidebar">
        <h2>Admin Menu</h2>
        <ul>
          <li onClick={() => setCurrentView("registeredUsers")}>Registered Users</li>
          <li onClick={() => setCurrentView("lostItems")}>Lost Items</li>
          <li onClick={() => setCurrentView("foundItems")}>Found Items</li>
          <li onClick={handleLogout} style={{ color: "red", cursor: "pointer" }}>Logout</li>
        </ul>
      </div>
      <div className="content">{renderView()}</div>
    </div>
  );
};

export default AdminDashboard;
