import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../styles/ProfilePage.css';
import UserNavbar from '../components/UserNavbar';

const ProfilePage = () => {
  const [user, setUser] = useState(null);
  const [postedItems, setPostedItems] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch user and their posted items
  useEffect(() => {
    const fetchUserData = async () => {
      try {
        // Get the user token from localStorage or context
        const token = localStorage.getItem('authToken');

        if (!token) {
          console.error('No authentication token found');
          return;
        }

        // Fetch user details
        const userResponse = await axios.get('http://localhost:5000/api/auth/profile', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        setUser(userResponse.data.user);

        // Fetch posted items by the user
        const itemsResponse = await axios.get('http://localhost:5000/api/items/user', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        setPostedItems(itemsResponse.data.items);
      } catch (error) {
        console.error('Error fetching user or items:', error.response?.data || error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchUserData();
  }, []);

  if (loading) return <p>Loading...</p>;

  return (
    <div className="profile-page">
      <UserNavbar />
      <div className="profile-container">
        <div className="profile-sidebar">
          <div className="profile-icon">
            <img
              src={user?.avatar || 'https://via.placeholder.com/100'}
              alt="User Avatar"
            />
          </div>
          <div className="user-info">
            <p><strong>Name:</strong> {user?.firstname} {user?.lastname}</p>
            <p><strong>Email:</strong> {user?.email}</p>
            <p><strong>User Type:</strong> {user?.userType}</p>
          </div>
        </div>

        <div className="profile-content">
          <h2>Posted Items</h2>
          <div className="posted-items">
            {postedItems.length > 0 ? (
              postedItems.map((item) => (
                <div key={item._id} className="posted-item">
                  <img src={item.image} alt={item.title} className="item-image" />
                  <p>{item.title}</p>
                </div>
              ))
            ) : (
              <p>No items posted yet.</p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProfilePage;
