// src/pages/AdminDashboard.jsx
import React from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/AdminDashboard.css';

const AdminDashboard = () => {
  const navigate = useNavigate();

  const handleLogout = () => {
    // Clear admin status from local storage to log out
    localStorage.removeItem('admin');
    navigate('/admin-login');  // Redirect to login page after logout
  };

  return (
    <div className="admin-dashboard">
      <div className="admin-navbar">
        <h2>Admin Dashboard</h2>
        <div className="admin-actions">
          <button onClick={handleLogout} className="logout-button">
            Logout
          </button>
        </div>
      </div>

      <div className="dashboard-content">
        <h3>Welcome, Admin!</h3>
        <p>This page is only accessible by the admin.</p>
        <p>You can manage your site, view statistics, or perform other administrative tasks here.</p>
        <div className="admin-dashboard-buttons">
          <button className="dashboard-button">Manage Users</button>
          <button className="dashboard-button">View Reports</button>
          <button className="dashboard-button">Settings</button>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
