import React, { useState, useEffect } from "react";
import axios from "axios";
import "../styles/AdminDashboard.css";

const AdminDashboard = () => {
  const [currentView, setCurrentView] = useState("addUser");
  const [users, setUsers] = useState([]);
  const [items, setItems] = useState([]);
  const [categoryFilter, setCategoryFilter] = useState("All");
  const [newUser, setNewUser] = useState({
    firstname: "",
    lastname: "",
    email: "",
    password: "",
    userType: "student",
  });
  const [editingUser, setEditingUser] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (currentView === "registeredUsers") fetchUsers();
    if (currentView === "lostItems" || currentView === "foundItems") fetchItems();
  }, [currentView, categoryFilter]);

  // Fetch Users
  const fetchUsers = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem("authToken");
      const response = await axios.get("http://localhost:5000/api/users/all-users", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUsers(response.data);
    } catch (err) {
      alert("Failed to fetch users.");
    } finally {
      setLoading(false);
    }
  };

  // Fetch Items (Lost or Found)
  const fetchItems = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem("authToken");
      const response = await axios.get("http://localhost:5000/api/items", {
        headers: { Authorization: `Bearer ${token}` },
      });

      const filteredItems = response.data.filter(
        (item) =>
          (currentView === "lostItems" ? item.postType === "Lost" : item.postType === "Found") &&
          (categoryFilter === "All" || item.category === categoryFilter)
      );
      setItems(filteredItems);
    } catch (err) {
      alert("Failed to fetch items.");
    } finally {
      setLoading(false);
    }
  };

  // Add New User
  const handleAddUser = async () => {
    try {
      const token = localStorage.getItem("authToken");
      await axios.post("http://localhost:5000/api/auth/register", newUser, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert("User added successfully!");
      setNewUser({ firstname: "", lastname: "", email: "", password: "", userType: "student" });
    } catch (err) {
      alert("Failed to add user.");
    }
  };

  // Update User
  const handleSaveEdit = async () => {
    try {
      const token = localStorage.getItem("authToken");
      await axios.put(`http://localhost:5000/api/users/${editingUser._id}`, editingUser, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert("User updated successfully!");
      setEditingUser(null);
      fetchUsers();
    } catch (err) {
      alert("Failed to update user.");
    }
  };

  // Delete User
  const handleDeleteUser = async (userId) => {
    try {
      const token = localStorage.getItem("authToken");
      await axios.delete(`http://localhost:5000/api/users/${userId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert("User deleted successfully!");
      fetchUsers();
    } catch (err) {
      alert("Failed to delete user.");
    }
  };

  // Logout
  const handleLogout = () => {
    localStorage.removeItem("authToken");
    window.location.href = "/login";
  };

  const renderView = () => {
    switch (currentView) {
      case "addUser":
        return (
          <div className="add-user-form">
            <h2>Add New User</h2>
            <input
              type="text"
              placeholder="First Name"
              value={newUser.firstname}
              onChange={(e) => setNewUser({ ...newUser, firstname: e.target.value })}
            />
            <input
              type="text"
              placeholder="Last Name"
              value={newUser.lastname}
              onChange={(e) => setNewUser({ ...newUser, lastname: e.target.value })}
            />
            <input
              type="email"
              placeholder="Email"
              value={newUser.email}
              onChange={(e) => setNewUser({ ...newUser, email: e.target.value })}
            />
            <input
              type="password"
              placeholder="Password"
              value={newUser.password}
              onChange={(e) => setNewUser({ ...newUser, password: e.target.value })}
            />
            <button onClick={handleAddUser}>Add User</button>
          </div>
        );
      case "registeredUsers":
        return (
          <div className="registered-users">
            <h2>Registered Users</h2>
            <table>
              <thead>
                <tr>
                  <th>First Name</th>
                  <th>Last Name</th>
                  <th>Email</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {users.map((user) => (
                  <tr key={user._id}>
                    {editingUser && editingUser._id === user._id ? (
                      <>
                        <td>
                          <input
                            value={editingUser.firstname}
                            onChange={(e) =>
                              setEditingUser({ ...editingUser, firstname: e.target.value })
                            }
                          />
                        </td>
                        <td>
                          <input
                            value={editingUser.lastname}
                            onChange={(e) =>
                              setEditingUser({ ...editingUser, lastname: e.target.value })
                            }
                          />
                        </td>
                        <td>
                          <button onClick={handleSaveEdit}>Save</button>
                          <button onClick={() => setEditingUser(null)}>Cancel</button>
                        </td>
                      </>
                    ) : (
                      <>
                        <td>{user.firstname}</td>
                        <td>{user.lastname}</td>
                        <td>{user.email}</td>
                        <td>
                          <button onClick={() => setEditingUser(user)}>Edit</button>
                          <button onClick={() => handleDeleteUser(user._id)}>Delete</button>
                          <a
                            href={`https://mail.google.com/mail/?view=cm&fs=1&to=${user.email}`}
                            target="_blank"
                            rel="noopener noreferrer"
                          >
                            Message
                          </a>
                        </td>
                      </>
                    )}
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        );
      default:
        return (
          <div>
            <h2>{currentView === "lostItems" ? "Lost Items" : "Found Items"}</h2>
            <select onChange={(e) => setCategoryFilter(e.target.value)}>
              <option value="All">All Categories</option>
              <option value="Electronics">Electronics</option>
              <option value="Personal Items">Personal Items</option>
              <option value="ID">ID</option>
              <option value="Other">Other</option>
            </select>
            <div className="items-display">
              {items.map((item) => (
                <div key={item._id} className="item-card">
                  {item.image && <img src={`http://localhost:5000/uploads/${item.image}`} alt="" />}
                  <p>{item.description}</p>
                  <p><strong>Category:</strong> {item.category}</p>
                  <p><strong>Posted By:</strong> {item.postedBy?.firstname || "Unknown"}</p>
                </div>
              ))}
            </div>
          </div>
        );
    }
  };

  return (
    <div className="admin-dashboard">
      <div className="left-sidebar">
        <h2>Admin Menu</h2>
        <ul>
          <li onClick={() => setCurrentView("addUser")}>Add User</li>
          <li onClick={() => setCurrentView("registeredUsers")}>Registered Users</li>
          <li onClick={() => setCurrentView("lostItems")}>Lost Items</li>
          <li onClick={() => setCurrentView("foundItems")}>Found Items</li>
          <li onClick={handleLogout} style={{ color: "red", cursor: "pointer" }}>
            Logout
          </li>
        </ul>
      </div>
      <div className="content">{renderView()}</div>
    </div>
  );
};

export default AdminDashboard;
