import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/UserHomePage.css';

const UserHomePage = () => {
  const [items, setItems] = useState([]);
  const [error, setError] = useState('');
  const [commentInput, setCommentInput] = useState({});

  useEffect(() => {
    fetchAllItems();
  }, []);

  const fetchAllItems = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        throw new Error('No token found. Please log in again.');
      }

      // Fetch items from the backend
      const response = await axios.get('http://localhost:5000/api/items', {
        headers: { Authorization: `Bearer ${token}` },
      });

      const userId = JSON.parse(atob(token.split('.')[1])).userId; // Decode userId from token
      const updatedItems = response.data.map((item) => ({
        ...item,
        isLiked: item.likedBy.includes(userId),
      }));

      setItems(updatedItems);
    } catch (err) {
      console.error('Error fetching items:', err.response?.data || err.message);
      setError('Failed to fetch items. Please try again.');
    }
  };

  const handleLike = async (itemId) => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        throw new Error('No token found. Please log in again.');
      }

      const response = await axios.post(
        `http://localhost:5000/api/items/${itemId}/like`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );

      const { likes, isLiked } = response.data;

      // Update the liked item's like count and status in the state
      setItems((prevItems) =>
        prevItems.map((item) =>
          item._id === itemId ? { ...item, likes, isLiked } : item
        )
      );
    } catch (err) {
      console.error('Error toggling like:', err.response?.data || err.message);
      alert(err.response?.data?.message || 'Failed to toggle like.');
    }
  };

  const handleComment = async (itemId) => {
    if (!commentInput[itemId]?.trim()) return;

    try {
      const token = localStorage.getItem('token');
      await axios.post(
        `http://localhost:5000/api/items/${itemId}/comment`,
        { comment: commentInput[itemId] },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setCommentInput((prevInput) => ({
        ...prevInput,
        [itemId]: '',
      }));
    } catch (err) {
      console.error('Error commenting on item:', err.response?.data || err.message);
    }
  };

  return (
    <div className="user-home">
      {/* Sidebar */}
      <div className="sidebar">
        <h2>Menu</h2>
        <ul>
          <li>Home</li>
          <li>Explore</li>
          <li>Notifications</li>
          <li>Messages</li>
          <li>Bookmarks</li>
          <li>Profile</li>
          <li>Settings</li>
        </ul>
        <button className="post-button">Post</button>
      </div>

      {/* Feed Section */}
      <div className="feed">
        <h1>For You</h1>
        {error && <p className="error">{error}</p>}
        <div className="items-feed">
          {items.length === 0 ? (
            <p>No items have been posted yet.</p>
          ) : (
            items.map((item) => (
              <div className="item-post" key={item._id}>
                <div className="post-header">
  <strong>
    {item.postedBy
      ? `${item.postedBy.firstname} ${item.postedBy.lastname}`
      : 'Unknown'}
  </strong>
  <span> • {new Date(item.createdAt).toLocaleDateString()}</span>
</div>

                <div className="post-body">
                  {item.image && (
                    <img
                      src={`http://localhost:5000/uploads/${item.image}`}
                      alt={item.description}
                      className="post-image"
                    />
                  )}
                  <p className="post-description">{item.description}</p>
                  <p className="post-category">
                    <strong>Category:</strong> {item.category}
                  </p>
                </div>
                <div className="post-actions">
                  <button
                    className={`like-button ${item.isLiked ? 'liked' : ''}`}
                    onClick={() => handleLike(item._id)}
                  >
                    ❤️ {item.likes}
                  </button>
                </div>
              </div>
            ))
          )}
        </div>
      </div>

      {/* Right Sidebar */}
      <div className="right-sidebar">
        <h3>What’s happening</h3>
        <ul>
          <li>#TrendingTopic1</li>
          <li>#TrendingTopic2</li>
          <li>#TrendingTopic3</li>
        </ul>
        <h3>Who to follow</h3>
        <ul>
          <li>John Doe</li>
          <li>Jane Smith</li>
          <li>Example User</li>
        </ul>
      </div>
    </div>
  );
};

export default UserHomePage;
