import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { GoogleLogin } from '@react-oauth/google';
import '../styles/Login.css';

const Login = () => {
  const [loginData, setLoginData] = useState({ email: '', password: '' });
  const navigate = useNavigate();

  const handleChange = (e) => {
    setLoginData({ ...loginData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Submitted login:', loginData);
    // Add your authentication logic here
    navigate('/user-home');
  };

  return (
    <div className="login-container">
      <div className="login-logo">
        <h1>X</h1>
      </div>
      <div className="login-content">
        <h2 className="login-title">Happening now</h2>
        <h3 className="login-subtitle">Join today.</h3>
        <div className="login-options">
          <GoogleLogin onSuccess={() => console.log('Google Login Success')} onError={() => console.log('Login Failed')} />
          <button className="apple-login">Sign up with Apple</button>
        </div>
        <div className="divider">or</div>
        <form className="login-form" onSubmit={handleSubmit}>
          <input
            type="email"
            name="email"
            placeholder="Email"
            value={loginData.email}
            onChange={handleChange}
            required
          />
          <button type="submit" className="create-account">Create account</button>
        </form>
        <p className="terms">
          By signing up, you agree to the <a href="#">Terms of Service</a> and <a href="#">Privacy Policy</a>.
        </p>
        <div className="signin-prompt">
          <p>Already have an account?</p>
          <a href="/login" className="signin-button">Sign in</a>
        </div>
      </div>
    </div>
  );
};

export default Login;
