import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { GoogleLogin } from '@react-oauth/google';
import axios from 'axios';
import '../styles/Login.css';

const Login = () => {
  const [loginData, setLoginData] = useState({
    email: '',
    password: '',
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    setLoginData({
      ...loginData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const { email, password } = loginData;

    try {
      const endpoint = 'http://localhost:5000/api/auth/login';
      const res = await axios.post(endpoint, { email, password });
      const { token, userType } = res.data;

      if (token) {
        localStorage.setItem('token', token);
        userType === 'admin' ? navigate('/admin') : navigate('/user-home');
      } else {
        alert('Login failed: Invalid response from server.');
      }
    } catch (error) {
      console.error('Error:', error.response?.data || error.message);
      alert('Login failed: ' + (error.response?.data?.message || error.message));
    }
  };

  return (
    <div className="login-page">
      <div className="login-container">
        <div className="login-form">
          <h2>LOGIN</h2>
          <p>How do I get started lorem ipsum dolor at?</p>
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <input
                type="email"
                name="email"
                placeholder="Username"
                value={loginData.email}
                onChange={handleChange}
                required
              />
            </div>
            <div className="form-group">
              <input
                type="password"
                name="password"
                placeholder="Password"
                value={loginData.password}
                onChange={handleChange}
                required
              />
            </div>
            <button type="submit" className="login-button">
              Login Now
            </button>
          </form>
          <div className="login-divider">
            <span>Login with Others</span>
          </div>
          <div className="social-login">
            <button className="google-btn">Login with Google</button>
            <button className="facebook-btn">Login with Facebook</button>
          </div>
        </div>
        <div className="login-image">
          {/* Placeholder Image */}
          <img
            src="https://via.placeholder.com/400x400"
            alt="Login Illustration"
          />
        </div>
      </div>
    </div>
  );
};

export default Login;
