import React, { useState, useEffect } from "react";
import axios from "axios";
import "../styles/AdminDashboard.css";

const AdminDashboard = () => {
  const [currentView, setCurrentView] = useState("addUser");
  const [users, setUsers] = useState([]);
  const [items, setItems] = useState([]);
  const [newUser, setNewUser] = useState({
    firstname: "",
    lastname: "",
    email: "",
    password: "",
    userType: "student",
  });
  const [editingUser, setEditingUser] = useState(null);
  const [filterCategory, setFilterCategory] = useState("All");

  useEffect(() => {
    if (currentView === "registeredUsers") fetchUsers();
    if (currentView === "lostItems" || currentView === "foundItems") fetchItems();
  }, [currentView]);

  // Fetch all users
  const fetchUsers = async () => {
    try {
      const token = localStorage.getItem("authToken");
      const response = await axios.get("http://localhost:5000/api/users/all-users", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUsers(response.data);
    } catch (err) {
      alert("Failed to fetch users.");
    }
  };

  // Fetch all items
  const fetchItems = async () => {
    try {
      const token = localStorage.getItem("authToken");
      const response = await axios.get("http://localhost:5000/api/items", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setItems(response.data);
    } catch (err) {
      alert("Failed to fetch items.");
    }
  };

  // Add a new user
  const addUser = async () => {
    try {
      const token = localStorage.getItem("authToken");
      await axios.post("http://localhost:5000/api/auth/register", newUser, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert("User added successfully!");
      setNewUser({ firstname: "", lastname: "", email: "", password: "", userType: "student" });
      fetchUsers();
    } catch (err) {
      alert("Failed to add user.");
    }
  };

  // Delete user
  const deleteUser = async (userId) => {
    try {
      const token = localStorage.getItem("authToken");
      await axios.delete(`http://localhost:5000/api/users/${userId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchUsers();
    } catch {
      alert("Failed to delete user.");
    }
  };

  // Update user
  const updateUser = async () => {
    try {
      const token = localStorage.getItem("authToken");
      await axios.put(`http://localhost:5000/api/users/${editingUser._id}`, editingUser, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setEditingUser(null);
      fetchUsers();
    } catch {
      alert("Failed to update user.");
    }
  };

  // Filter Lost/Found items
  const filteredItems = items.filter((item) => {
    const matchesType =
      (currentView === "lostItems" && item.postType === "Lost") ||
      (currentView === "foundItems" && item.postType === "Found");
    const matchesCategory = filterCategory === "All" || item.category === filterCategory;
    return matchesType && matchesCategory;
  });

  const renderContent = () => {
    switch (currentView) {
      case "addUser":
        return (
          <div className="add-user-form">
            <h2>Add New User</h2>
            <input
              type="text"
              placeholder="First Name"
              value={newUser.firstname}
              onChange={(e) => setNewUser({ ...newUser, firstname: e.target.value })}
            />
            <input
              type="text"
              placeholder="Last Name"
              value={newUser.lastname}
              onChange={(e) => setNewUser({ ...newUser, lastname: e.target.value })}
            />
            <input
              type="email"
              placeholder="Email"
              value={newUser.email}
              onChange={(e) => setNewUser({ ...newUser, email: e.target.value })}
            />
            <input
              type="password"
              placeholder="Password"
              value={newUser.password}
              onChange={(e) => setNewUser({ ...newUser, password: e.target.value })}
            />
            <button onClick={addUser}>Add User</button>
          </div>
        );

      case "registeredUsers":
        return (
          <div className="registered-users">
            <h2>Registered Users</h2>
            <table>
              <thead>
                <tr>
                  <th>First Name</th>
                  <th>Last Name</th>
                  <th>Email</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {users.map((user) => (
                  <tr key={user._id}>
                    <td>{user.firstname}</td>
                    <td>{user.lastname}</td>
                    <td>{user.email}</td>
                    <td>
                      <button onClick={() => setEditingUser(user)}>
                        <i className="fa-solid fa-pen-to-square"></i>
                      </button>
                      <button onClick={() => deleteUser(user._id)}>
                        <i className="fa-solid fa-trash"></i>
                      </button>
                      <a href={`mailto:${user.email}`} target="_blank" rel="noopener noreferrer">
                        <button>
                          <i className="fa-solid fa-envelope"></i>
                        </button>
                      </a>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        );

      case "lostItems":
      case "foundItems":
        return (
          <div className="items-view">
            <h2>{currentView === "lostItems" ? "Lost Items" : "Found Items"}</h2>
            <select onChange={(e) => setFilterCategory(e.target.value)} value={filterCategory}>
              <option value="All">All Categories</option>
              <option value="Electronics">Electronics</option>
              <option value="Personal-item">Personal Items</option>
              <option value="ID">ID</option>
              <option value="Other">Other</option>
            </select>
            <div className="items-feed">
              {filteredItems.length > 0 ? (
                filteredItems.map((item) => (
                  <div key={item._id} className="item-post">
                    <strong>{item.description}</strong>
                    <p>
                      <strong>Category:</strong> {item.category}
                    </p>
                  </div>
                ))
              ) : (
                <p>No items found.</p>
              )}
            </div>
          </div>
        );

      default:
        return <h2>Welcome to Admin Dashboard</h2>;
    }
  };

  return (
    <div className="admin-dashboard">
      <div className="sidebar">
        <h2>Admin Menu</h2>
        <ul>
          <li onClick={() => setCurrentView("addUser")}>Add User</li>
          <li onClick={() => setCurrentView("registeredUsers")}>Registered Users</li>
          <li onClick={() => setCurrentView("lostItems")}>Lost Items</li>
          <li onClick={() => setCurrentView("foundItems")}>Found Items</li>
          <li onClick={() => setCurrentView("category")}>Category</li>
          <li>
            <a href="/logout">Logout</a>
          </li>
        </ul>
      </div>
      <div className="content">{renderContent()}</div>
    </div>
  );
};

export default AdminDashboard;
