import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/AdminDashboard.css';

const AdminDashboard = () => {
  const [users, setUsers] = useState([]);
  const [newUser, setNewUser] = useState({ email: '', password: '', userType: 'student' });
  const [editingUser, setEditingUser] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    setLoading(true);
    setError(null);
    try {
      const token = localStorage.getItem('authToken');
      if (!token) {
        throw new Error('No token found. Please log in again.');
      }

      const response = await axios.get('http://localhost:5000/api/users/all-users', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUsers(response.data);
    } catch (err) {
      console.error('Error fetching users:', err.response?.data || err.message);
      setError(err.response?.data?.message || 'Failed to fetch users.');
    } finally {
      setLoading(false);
    }
  };

  const addUser = async () => {
    setError(null);
    try {
      const token = localStorage.getItem('authToken');
      if (!newUser.email || !newUser.password || !newUser.userType) {
        alert('All fields are required.');
        return;
      }

      const response = await axios.post(
        'http://localhost:5000/api/auth/register',
        newUser,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      alert(response.data.message);
      setNewUser({ email: '', password: '', userType: 'student' });
      fetchUsers(); // Refresh user list
    } catch (err) {
      console.error('Error adding user:', err.response?.data || err.message);
      alert(err.response?.data?.message || 'Failed to add user.');
    }
  };

  const deleteUser = async (userId) => {
    try {
      const token = localStorage.getItem('authToken');
      const response = await axios.delete(`http://localhost:5000/api/users/${userId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      alert(response.data.message);
      fetchUsers(); // Refresh user list
    } catch (err) {
      console.error('Error deleting user:', err.response?.data || err.message);
      alert(err.response?.data?.message || 'Failed to delete user.');
    }
  };

  const updateUser = async (userId, updatedUser) => {
    try {
      const token = localStorage.getItem('authToken');
      const response = await axios.put(
        `http://localhost:5000/api/users/${userId}`,
        updatedUser,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      alert(response.data.message);
      setEditingUser(null);
      fetchUsers(); // Refresh user list
    } catch (err) {
      console.error('Error updating user:', err.response?.data || err.message);
      alert(err.response?.data?.message || 'Failed to update user.');
    }
  };

  return (
    <div className="admin-dashboard">
      <h1>Admin Dashboard</h1>

      <div className="add-user-form">
        <h2>Add New User</h2>
        <input
          type="email"
          placeholder="Email"
          value={newUser.email}
          onChange={(e) => setNewUser({ ...newUser, email: e.target.value })}
        />
        <input
          type="password"
          placeholder="Password"
          value={newUser.password}
          onChange={(e) => setNewUser({ ...newUser, password: e.target.value })}
        />
        <select
          value={newUser.userType}
          onChange={(e) => setNewUser({ ...newUser, userType: e.target.value })}
        >
          <option value="student">Student</option>
          <option value="staff">Staff</option>
          <option value="faculty">Faculty</option>
          <option value="admin">Admin</option>
        </select>
        <button onClick={addUser}>Add User</button>
      </div>

      <div className="registered-users">
        <h2>Registered Users</h2>
        {loading && <p>Loading users...</p>}
        {error && <p className="error-message">{error}</p>}
        {!loading && !error && users.length === 0 && <p>No registered users found.</p>}
        {!loading && users.length > 0 && (
          <table>
            <thead>
              <tr>
                <th>Email</th>
                <th>User Type</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
            {users.map((user) => (
  <tr key={user._id}>
    <td>
      {editingUser && editingUser._id === user._id ? (
        <input
          type="email"
          defaultValue={user.email}
          onChange={(e) =>
            setEditingUser({ ...editingUser, email: e.target.value })
          }
        />
      ) : (
        user.email
      )}
    </td>
    <td>
      {editingUser && editingUser._id === user._id ? (
        <select
          defaultValue={user.userType}
          onChange={(e) =>
            setEditingUser({ ...editingUser, userType: e.target.value })
          }
        >
          <option value="student">Student</option>
          <option value="staff">Staff</option>
          <option value="faculty">Faculty</option>
          <option value="admin">Admin</option>
        </select>
      ) : (
        user.userType
      )}
    </td>
    <td>
      {editingUser && editingUser._id === user._id ? (
        <button onClick={saveEditedUser}>Save</button>
      ) : (
        <>
          <button onClick={() => setEditingUser(user)}>Edit</button>
          <button onClick={() => deleteUser(user._id)}>Delete</button>
        </>
      )}
    </td>
  </tr>
))}

            </tbody>
          </table>
        )}
      </div>
    </div>
  );
};

export default AdminDashboard;
