import React, { useState, useEffect } from "react";
import axios from "axios";
import "../styles/AdminDashboard.css";

const AdminDashboard = () => {
  const [users, setUsers] = useState([]);
  const [items, setItems] = useState([]); // Store Lost and Found items
  const [filterType, setFilterType] = useState("All"); // For Lost/Found filter
  const [filterCategory, setFilterCategory] = useState("All"); // For category filter
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchUsers();
    fetchItems();
  }, []);

  // Fetch all registered users
  const fetchUsers = async () => {
    try {
      const token = localStorage.getItem("authToken");
      if (!token) throw new Error("No token found. Please log in again.");

      const response = await axios.get("http://localhost:5000/api/users/all-users", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUsers(response.data);
    } catch (err) {
      console.error("Error fetching users:", err.response?.data || err.message);
      setError(err.response?.data?.message || "Failed to fetch users.");
    }
  };

  // Fetch all Lost and Found items
  const fetchItems = async () => {
    try {
      const token = localStorage.getItem("authToken");
      if (!token) throw new Error("No token found. Please log in again.");

      const response = await axios.get("http://localhost:5000/api/items", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setItems(response.data);
    } catch (err) {
      console.error("Error fetching items:", err.response?.data || err.message);
      setError(err.response?.data?.message || "Failed to fetch items.");
    }
  };

  // Filtered items based on the selected filterType and filterCategory
  const filteredItems = items.filter((item) => {
    const matchesType = filterType === "All" || item.postType === filterType;
    const matchesCategory =
      filterCategory === "All" || item.category === filterCategory;
    return matchesType && matchesCategory;
  });

  const handleFilterChange = (type) => {
    setFilterType(type);
    setFilterCategory("All"); // Reset category filter when changing the menu filter
  };

  const handleCategoryChange = (category) => {
    setFilterCategory(category);
    setFilterType("All"); // Reset menu filter when changing the category filter
  };

  const handleLogout = () => {
    localStorage.removeItem("authToken");
    window.location.href = "/login"; // Redirect to login page
  };

  return (
    <div className="admin-dashboard">
      <nav className="navbar">
        <div className="navbar-brand">Admin Dashboard</div>
        <ul className="navbar-links">
          <li>
            <button className="logout-btn" onClick={handleLogout}>
              Logout
            </button>
          </li>
        </ul>
      </nav>

      <div className="content">
        {/* Filters Section */}
        <div className="filters">
          <h2>Filters</h2>
          <div className="filter-group">
            <button onClick={() => handleFilterChange("All")}>All</button>
            <button onClick={() => handleFilterChange("Lost")}>Lost</button>
            <button onClick={() => handleFilterChange("Found")}>Found</button>
          </div>
          <div className="filter-group">
            <h3>Categories</h3>
            <button onClick={() => handleCategoryChange("All")}>All Categories</button>
            <button onClick={() => handleCategoryChange("Electronics")}>Electronics</button>
            <button onClick={() => handleCategoryChange("Personal-item")}>
              Personal Items
            </button>
            <button onClick={() => handleCategoryChange("ID")}>ID</button>
            <button onClick={() => handleCategoryChange("Other")}>Other</button>
          </div>
        </div>

        {/* Lost and Found Items Section */}
        <div className="lost-found-items">
          <h2>Lost and Found Items</h2>
          {loading && <p>Loading items...</p>}
          {error && <p className="error-message">{error}</p>}
          <div className="items-feed">
            {filteredItems.length === 0 ? (
              <p>No items found for this filter.</p>
            ) : (
              filteredItems.map((item) => (
                <div className="item-post" key={item._id}>
                  <div className="post-header">
                    <strong>
                      {item.postedBy
                        ? `${item.postedBy.firstname} ${item.postedBy.lastname}`
                        : "Unknown"}
                    </strong>
                    <span> • {new Date(item.createdAt).toLocaleDateString()}</span>
                  </div>
                  <div className="post-body">
                    {item.image && (
                      <img
                        src={`http://localhost:5000/uploads/${item.image}`}
                        alt={item.description}
                        className="post-image"
                      />
                    )}
                    <p className="post-description">{item.description}</p>
                    <p className="post-category">
                      <strong>Category:</strong> {item.category}
                    </p>
                    <p className="post-type">
                      <strong>Post Type:</strong> {item.postType}
                    </p>
                  </div>
                </div>
              ))
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
