// components/UserNavbar.jsx
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import '../styles/UserNavbar.css';

const UserNavbar = () => {
  const [dropdown, setDropdown] = useState(false);

  const toggleDropdown = () => {
    setDropdown(!dropdown);
  };

  const handleLogout = () => {
    // Clear the token from localStorage
    localStorage.removeItem('token');
    
    // Use navigate to redirect without reloading the page
    navigate('/login');
  };

  return (
    <nav className="user-navbar">
      <div className="logo">
        <h1>BukSU Lost and Found</h1>
      </div>
      <ul className="nav-links">
        <li><Link to="/user-home">Home</Link></li>
        <li className="dropdown">
          <button onClick={toggleDropdown} className="dropdown-btn">Category</button>
          {dropdown && (
            <ul className="dropdown-menu">
              <li><Link to="/category/electronics">Electronics</Link></li>
              <li><Link to="/category/clothing">Clothing</Link></li>
              <li><Link to="/category/books">Books</Link></li>
              {/* Add more categories as needed */}
            </ul>
          )}
        </li>
        <li><Link to="/about">About Us</Link></li>
        <li><Link to="/contact">Contact Us</Link></li>
        <li><Link to="/profile"><i className="user-icon">ðŸ‘¤</i></Link></li>
        <li><button onClick={handleLogout} className="logout-btn">Logout</button></li>
      </ul>
    </nav>
  );
};

export default UserNavbar;
