import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Link, useNavigate } from 'react-router-dom';
import Home from './pages/Home';
import About from './pages/About';
import Login from './pages/Login';
import Register from './pages/Register';
import UserHome from './pages/UserHomePage'; // Import UserHome
import ProtectedRoute from './components/ProtectedRoute'; // Import ProtectedRoute
import './App.css';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    // Check if a token exists in localStorage to determine if user is authenticated
    const token = localStorage.getItem('userToken');
    if (token) {
      setIsAuthenticated(true); // User is logged in
    }
  }, []);

  const handleLogout = () => {
    // Clear the token from localStorage
    localStorage.removeItem('userToken');
    setIsAuthenticated(false); // Update the state
    navigate('/login'); // Redirect to the login page
  };

  return (
    <Router>
      <div className="App">
        <header>
          <h1>BukSU Lost and Found System</h1>
          <nav>
            <ul>
              <li><Link to="/">Home</Link></li>
              <li><Link to="/about">About</Link></li>
              {isAuthenticated ? (
                <>
                  <li><Link to="/user-home">User Home</Link></li>
                  <li><button onClick={handleLogout}>Logout</button></li>
                </>
              ) : (
                <>
                  <li><Link to="/login">Login</Link></li>
                  <li><Link to="/register">Register</Link></li>
                </>
              )}
            </ul>
          </nav>
        </header>

        <main>
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/about" element={<About />} />
            <Route path="/login" element={<Login />} />
            <Route path="/register" element={<Register />} />
            {/* Protect the /user-home route */}
            <Route
              path="/user-home"
              element={
                <ProtectedRoute>
                  <UserHome />
                </ProtectedRoute>
              }
            />
          </Routes>
        </main>

        <footer>
          <p>&copy; 2024 BukSU Lost and Found System</p>
        </footer>
      </div>
    </Router>
  );
}

export default App;
