import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '.styles/AdminDashboard.css';

const AdminDashboard = () => {
  const [users, setUsers] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [newUser, setNewUser] = useState({ email: '', userType: '' });
  const [editingUser, setEditingUser] = useState(null);

  const categories = ['Electronics', 'Personal Items', 'IDs', 'Others'];

  const fetchUsers = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('authToken');
      if (!token) {
        setError('No token found. Please log in.');
        setLoading(false);
        return;
      }

      const response = await axios.get('http://localhost:5000/api/users/all-users', {
        headers: { Authorization: `Bearer ${token}` },
      });

      setUsers(response.data);
      setError(null);
    } catch (err) {
      console.error('Error fetching users:', err);
      setError(err.response?.data?.message || 'Failed to fetch users');
    } finally {
      setLoading(false);
    }
  };

  const addUser = async () => {
    try {
      const token = localStorage.getItem('authToken');
      const response = await axios.post(
        'http://localhost:5000/api/auth/register',
        newUser,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      alert(response.data.message);
      fetchUsers();
    } catch (err) {
      console.error('Error adding user:', err);
      alert(err.response?.data?.message || 'Failed to add user');
    }
  };

  const deleteUser = async (userId) => {
    try {
      const token = localStorage.getItem('authToken');
      await axios.delete(`http://localhost:5000/api/users/user/${userId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert('User deleted successfully');
      fetchUsers();
    } catch (err) {
      console.error('Error deleting user:', err);
      alert(err.response?.data?.message || 'Failed to delete user');
    }
  };

  const updateUser = async () => {
    try {
      const token = localStorage.getItem('authToken');
      await axios.put(
        `http://localhost:5000/api/users/user/${editingUser._id}`,
        editingUser,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      alert('User updated successfully');
      setEditingUser(null);
      fetchUsers();
    } catch (err) {
      console.error('Error updating user:', err);
      alert(err.response?.data?.message || 'Failed to update user');
    }
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  return (
    <div className="admin-dashboard">
      {/* Navbar */}
      <nav className="navbar">
        <h1>Admin Dashboard</h1>
        <ul>
          {categories.map((category, index) => (
            <li key={index}>
              <a href={`/category/${category.toLowerCase().replace(/ /g, '-')}`}>
                {category}
              </a>
            </li>
          ))}
        </ul>
      </nav>

      <div className="content">
        <h2>Manage Users</h2>
        {loading && <p>Loading...</p>}
        {error && <p style={{ color: 'red' }}>{error}</p>}

        {/* User Table */}
        <table>
          <thead>
            <tr>
              <th>Email</th>
              <th>User Type</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {users.map((user) => (
              <tr key={user._id}>
                <td>{user.email}</td>
                <td>{user.userType}</td>
                <td>
                  <button onClick={() => deleteUser(user._id)}>Delete</button>
                  <button onClick={() => setEditingUser(user)}>Edit</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        {/* Add New User */}
        <div>
          <h3>Add New User</h3>
          <input
            type="email"
            placeholder="Email"
            value={newUser.email}
            onChange={(e) => setNewUser({ ...newUser, email: e.target.value })}
          />
          <select
            value={newUser.userType}
            onChange={(e) => setNewUser({ ...newUser, userType: e.target.value })}
          >
            <option value="">Select User Type</option>
            <option value="student">Student</option>
            <option value="staff">Staff</option>
            <option value="faculty">Faculty</option>
          </select>
          <button onClick={addUser}>Add User</button>
        </div>

        {/* Edit User */}
        {editingUser && (
          <div>
            <h3>Edit User</h3>
            <input
              type="email"
              placeholder="Email"
              value={editingUser.email}
              onChange={(e) =>
                setEditingUser({ ...editingUser, email: e.target.value })
              }
            />
            <select
              value={editingUser.userType}
              onChange={(e) =>
                setEditingUser({ ...editingUser, userType: e.target.value })
              }
            >
              <option value="student">Student</option>
              <option value="staff">Staff</option>
              <option value="faculty">Faculty</option>
            </select>
            <button onClick={updateUser}>Save Changes</button>
            <button onClick={() => setEditingUser(null)}>Cancel</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminDashboard;
