import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AdminDashboard = () => {
  const [users, setUsers] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  // State for user creation
  const [newUser, setNewUser] = useState({ email: '', userType: '' });

  // State for editing user
  const [editingUser, setEditingUser] = useState(null);

  // Fetch all users
  const fetchUsers = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('authToken');
      if (!token) {
        setError('No token found. Please log in.');
        setLoading(false);
        return;
      }

      const response = await axios.get('http://localhost:5000/api/users/all-users', {
        headers: { Authorization: `Bearer ${token}` },
      });

      setUsers(response.data);
      setError(null);
    } catch (err) {
      console.error('Error fetching users:', err);
      setError(err.response?.data?.message || 'Failed to fetch users');
    } finally {
      setLoading(false);
    }
  };

  // Add a new user
  const addUser = async () => {
    try {
      const token = localStorage.getItem('authToken');
      const response = await axios.post(
        'http://localhost:5000/api/auth/register',
        newUser,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      alert(response.data.message);
      fetchUsers(); // Refresh user list
    } catch (err) {
      console.error('Error adding user:', err);
      alert(err.response?.data?.message || 'Failed to add user');
    }
  };

  // Delete a user
  const deleteUser = async (userId) => {
    try {
      const token = localStorage.getItem('authToken');
      await axios.delete(`http://localhost:5000/api/users/user/${userId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert('User deleted successfully');
      fetchUsers(); // Refresh user list
    } catch (err) {
      console.error('Error deleting user:', err);
      alert(err.response?.data?.message || 'Failed to delete user');
    }
  };

  // Edit user
  const updateUser = async () => {
    try {
      const token = localStorage.getItem('authToken');
      await axios.put(
        `http://localhost:5000/api/users/user/${editingUser._id}`,
        editingUser,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      alert('User updated successfully');
      setEditingUser(null); // Clear edit state
      fetchUsers(); // Refresh user list
    } catch (err) {
      console.error('Error updating user:', err);
      alert(err.response?.data?.message || 'Failed to update user');
    }
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  return (
    <div>
      <h1>Admin Dashboard</h1>
      {loading && <p>Loading...</p>}
      {error && <p style={{ color: 'red' }}>{error}</p>}

      {/* User List */}
      <table>
        <thead>
          <tr>
            <th>Email</th>
            <th>User Type</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user._id}>
              <td>{user.email}</td>
              <td>{user.userType}</td>
              <td>
                <button onClick={() => deleteUser(user._id)}>Delete</button>
                <button onClick={() => setEditingUser(user)}>Edit</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Add New User */}
      <div>
        <h2>Add New User</h2>
        <input
          type="email"
          placeholder="Email"
          value={newUser.email}
          onChange={(e) => setNewUser({ ...newUser, email: e.target.value })}
        />
        <select
          value={newUser.userType}
          onChange={(e) => setNewUser({ ...newUser, userType: e.target.value })}
        >
          <option value="">Select User Type</option>
          <option value="student">Student</option>
          <option value="staff">Staff</option>
          <option value="faculty">Faculty</option>
        </select>
        <button onClick={addUser}>Add User</button>
      </div>

      {/* Edit User */}
      {editingUser && (
        <div>
          <h2>Edit User</h2>
          <input
            type="email"
            placeholder="Email"
            value={editingUser.email}
            onChange={(e) =>
              setEditingUser({ ...editingUser, email: e.target.value })
            }
          />
          <select
            value={editingUser.userType}
            onChange={(e) =>
              setEditingUser({ ...editingUser, userType: e.target.value })
            }
          >
            <option value="student">Student</option>
            <option value="staff">Staff</option>
            <option value="faculty">Faculty</option>
          </select>
          <button onClick={updateUser}>Save Changes</button>
          <button onClick={() => setEditingUser(null)}>Cancel</button>
        </div>
      )}
    </div>
  );
};

export default AdminDashboard;
