// src/components/UserNavbar.jsx
import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';  // Import useNavigate
import '../styles/UserNavbar.css';

const UserNavbar = () => {
  const [dropdownOpen, setDropdownOpen] = useState(false); // State to control dropdown visibility
  const navigate = useNavigate();  // Initialize useNavigate to redirect
  const categories = ['Electronics', 'Personal Items', 'IDs', 'Umbrellas & Water Bottles', 'Clothing', 'Other'];

  const toggleDropdown = () => {
    setDropdownOpen(!dropdownOpen); // Toggle dropdown visibility
  };

  const handleLogout = () => {
    localStorage.removeItem('token');  // Remove token or any other session data
    navigate('/login');  // Redirect to login page
  };

  return (
    <nav className="user-navbar">
      <div className="navbar-logo">
        <h1>BukSU Lost and Found</h1>
      </div>
      <div className="navbar-links">
        <ul>
          <li><Link to="/user-home">Home</Link></li>
          
          {/* Dropdown for Categories */}
          <li className={`dropdown ${dropdownOpen ? 'open' : ''}`} onClick={toggleDropdown}>
            <span>Item Categories</span>
            <ul className="dropdown-menu">
              {categories.map((category, index) => (
                <li key={index}>
                  <Link to={`/category/${category.toLowerCase().replace(/ & /g, '-').replace(/ /g, '-')}`}>
                    {category}
                  </Link>
                </li>
              ))}
            </ul>
          </li>
          
          <li><Link to="/about">About Us</Link></li>
          <li><Link to="/contact">Contact Us</Link></li>
          <li><Link to="/profile"><img src="user-icon.png" alt="Profile" /></Link></li>
          
          {/* Log Out Button */}
          <li><button onClick={handleLogout} className="logout-button">Logout</button></li>
        </ul>
      </div>
    </nav>
  );
};

export default UserNavbar;
