import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import UserNavbar from '../components/UserNavbar';
import '../styles/PostItemPage.css';

const PostItemPage = () => {
  const [image, setImage] = useState(null);
  const [description, setDescription] = useState('');
  const [category, setCategory] = useState('');
  const [error, setError] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccessMessage('');

    if (!image || !description || !category) {
      setError('All fields are required.');
      return;
    }

    const formData = new FormData();
    formData.append('image', image);
    formData.append('description', description);
    formData.append('category', category);

    try {
      const token = localStorage.getItem('token');
      if (!token) {
        throw new Error('No token found. Please log in again.');
      }

      const response = await axios.post('http://localhost:5000/api/items', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
          Authorization: `Bearer ${token}`,
        },
      });

      setSuccessMessage(response.data.message || 'Item posted successfully!');
      setTimeout(() => navigate('/profile'), 2000);
    } catch (error) {
      console.error('Error posting item:', error.response?.data || error.message);
      setError(error.response?.data?.message || 'Failed to post the item. Please try again.');
    }
  };

  return (
    <div className="post-item-container">
      <UserNavbar />
      <div className="post-item-card">
        <h2>Create a Post</h2>
        <form onSubmit={handleSubmit} className="post-item-form">
          {error && <p className="error">{error}</p>}
          {successMessage && <p className="success">{successMessage}</p>}

          <div className="form-section">
            <label>
              Add an Image
              <input
                type="file"
                accept="image/*"
                onChange={(e) => setImage(e.target.files[0])}
                required
              />
            </label>
          </div>

          <div className="form-section">
            <textarea
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              placeholder="What's on your mind?"
              required
            />
          </div>

          <div className="form-section">
            <label>
              Category
              <select
                value={category}
                onChange={(e) => setCategory(e.target.value)}
                required
              >
                <option value="">Choose a category</option>
                <option value="Electronics">Electronics</option>
                <option value="Personal-item">Personal Item</option>
                <option value="ID">ID</option>
                <option value="Other">Other</option>
              </select>
            </label>
          </div>

          <div className="form-actions">
            <button type="submit">Post</button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default PostItemPage;
