import React, { useState } from 'react';
import { GoogleOAuthProvider, GoogleLogin } from '@react-oauth/google';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import ReCAPTCHA from 'react-google-recaptcha'; // Import ReCAPTCHA component
import '../styles/Register.css';

const Register = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    firstname: '',
    lastname: '',
    email: '',
    password: '',
    userType: 'student',
  });
  const [recaptchaValue, setRecaptchaValue] = useState(null); // Store reCAPTCHA value

  // Handle reCAPTCHA change
  const onRecaptchaChange = (value) => {
    setRecaptchaValue(value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Check if reCAPTCHA is validated
    if (!recaptchaValue) {
      alert('Please verify that you are not a robot');
      return;
    }

    try {
      const response = await axios.post('http://localhost:5000/api/register', formData);
      alert('User registered successfully');
      navigate('/login'); // Navigate to login on success
    } catch (error) {
      console.error('Registration failed', error);
    }
  };

  const handleGoogleSuccess = async (credentialResponse) => {
    try {
      // Send the Google credential to the backend for verification and registration
      const response = await axios.post('http://localhost:5000/api/auth/google', {
        token: credentialResponse.credential,
      });
      if (response.data.success) {
        navigate('/user-home');
      }
    } catch (error) {
      console.error("Google signup failed", error);
    }
  };

  return (
    <GoogleOAuthProvider clientId="475680109157-79nfok0c0hfhilhddhljp3jk1jqh02vc.apps.googleusercontent.com">
      <div className="register">
        <h2>Create an Account</h2>
        <form onSubmit={handleSubmit}>
          {/* Registration form fields */}
          <input
            type="text"
            placeholder="First Name"
            value={formData.firstname}
            onChange={(e) => setFormData({ ...formData, firstname: e.target.value })}
          />
          <input
            type="text"
            placeholder="Last Name"
            value={formData.lastname}
            onChange={(e) => setFormData({ ...formData, lastname: e.target.value })}
          />
          <input
            type="email"
            placeholder="Email"
            value={formData.email}
            onChange={(e) => setFormData({ ...formData, email: e.target.value })}
          />
          <input
            type="password"
            placeholder="Password"
            value={formData.password}
            onChange={(e) => setFormData({ ...formData, password: e.target.value })}
          />
          <select
            value={formData.userType}
            onChange={(e) => setFormData({ ...formData, userType: e.target.value })}
          >
            <option value="student">Student</option>
            <option value="staff">Staff</option>
            <option value="faculty">Faculty</option>
          </select>

          {/* Add reCAPTCHA here */}
          <ReCAPTCHA
            sitekey="6LeWFX4qAAAAALBsKNJ-PITPdlzrAb5zH3aHs8bz" // Your site key
            onChange={onRecaptchaChange}
          />

          <button type="submit">Register</button>
        </form>
        <div className="divider">or</div>
        <GoogleLogin
          onSuccess={handleGoogleSuccess}
          onError={() => console.log('Google login failed')}
        />
      </div>
    </GoogleOAuthProvider>
  );
};

export default Register;
