import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import '../styles/AdminDashboard.css';

const AdminDashboard = () => {
  const navigate = useNavigate();
  const { userId } = useParams(); // For editing user
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [formData, setFormData] = useState({
    email: '',
    userType: 'student',
  });

  // Fetch users
  useEffect(() => {
    const fetchUsers = async () => {
      try {
        setLoading(true);
        const response = await axios.get('http://localhost:5000/api/users');
        setUsers(response.data);
      } catch (error) {
        setError('Failed to fetch users.');
      } finally {
        setLoading(false);
      }
    };

    fetchUsers();
  }, []);

  // Fetch user data for editing
  useEffect(() => {
    if (userId) {
      const fetchUser = async () => {
        try {
          const response = await axios.get(`http://localhost:5000/api/users/${userId}`);
          setFormData(response.data);
        } catch (error) {
          setError('Failed to fetch user data.');
        }
      };
      fetchUser();
    }
  }, [userId]);

  const handleLogout = () => {
    localStorage.removeItem('admin');
    navigate('/admin-login');
  };

  const handleDeleteUser = async (id) => {
    if (!window.confirm('Are you sure you want to delete this user?')) return;
    try {
      await axios.delete(`http://localhost:5000/api/users/${id}`);
      setUsers(users.filter((user) => user._id !== id));
    } catch (error) {
      setError('Failed to delete user.');
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (userId) {
        await axios.put(`http://localhost:5000/api/users/${userId}`, formData);
        alert('User updated successfully!');
      } else {
        await axios.post('http://localhost:5000/api/users', formData);
        alert('User created successfully!');
      }
      navigate('/admin-dashboard');
    } catch (error) {
      setError('Failed to submit user data.');
    }
  };

  return (
    <div className="admin-dashboard">
      <div className="admin-navbar">
        <h2>Admin Dashboard</h2>
        <button onClick={handleLogout} className="logout-button">Logout</button>
      </div>
      <div className="dashboard-content">
        {error && <p className="error">{error}</p>}
        <h3>{userId ? 'Edit User' : 'Create New User'}</h3>
        <form onSubmit={handleSubmit} className="user-form">
          <label>Email</label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleInputChange}
            required
          />
          <label>User Type</label>
          <select
            name="userType"
            value={formData.userType}
            onChange={handleInputChange}
            required
          >
            <option value="student">Student</option>
            <option value="staff">Staff</option>
            <option value="faculty">Faculty</option>
          </select>
          <button type="submit" className="submit-button">
            {userId ? 'Update User' : 'Create User'}
          </button>
        </form>
        <h4>Users List</h4>
        {loading ? (
          <p>Loading...</p>
        ) : (
          <table className="users-table">
            <thead>
              <tr>
                <th>Email</th>
                <th>User Type</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {users.map((user) => (
                <tr key={user._id}>
                  <td>{user.email}</td>
                  <td>{user.userType}</td>
                  <td>
                    <button onClick={() => navigate(`/admin-edit-user/${user._id}`)}>Edit</button>
                    <button onClick={() => handleDeleteUser(user._id)}>Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
};

export default AdminDashboard;
