import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import UserNavbar from '../components/UserNavbar';
import '../styles/ProfilePage.css';

const ProfilePage = () => {
  const [user, setUser] = useState({});
  const [postedItems, setPostedItems] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchUserAndItems = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          setError('Authentication token not found. Please log in.');
          setLoading(false);
          return;
        }

        // Fetch user info from the token
        const userResponse = await axios.get('http://localhost:5000/api/auth/profile', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setUser(userResponse.data.user);

        // Fetch user's posted items
        const itemsResponse = await axios.get('http://localhost:5000/api/items/user', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setPostedItems(itemsResponse.data.items);
      } catch (err) {
        console.error('Error fetching data:', err.response?.data || err.message);
        setError('Failed to load your profile or items. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchUserAndItems();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('user');
    localStorage.removeItem('token');
    window.location.href = '/login';
  };

  const handleDeleteAccount = async () => {
    if (!window.confirm('Are you sure you want to delete your account? This action cannot be undone.')) {
      return;
    }

    try {
      const token = localStorage.getItem('token');
      await axios.delete('http://localhost:5000/api/delete-account', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      alert('Your account has been successfully deleted.');
      localStorage.removeItem('user');
      localStorage.removeItem('token');
      window.location.href = '/login';
    } catch (error) {
      console.error('Error deleting account:', error.response?.data || error.message);
      alert('An error occurred while deleting your account. Please try again.');
    }
  };

  const fetchProfile = async () => {
    useEffect(() => {
      const fetchProfile = async () => {
        try {
          const token = localStorage.getItem('token');
          const response = await axios.get('http://localhost:5000/api/auth/profile', {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          });
          setUser(response.data.user);
        } catch (error) {
          console.error('Error fetching data:', error.response?.data || error.message);
        }
      };
    
      fetchProfile();
    }, []);
    
  };
  

  if (loading) return <p>Loading...</p>;

  return (
    <div className="profile-page">
      <UserNavbar />
      <div className="profile-container">
        {/* Left Sidebar */}
        <div className="profile-sidebar">
          <div className="profile-icon">
            <img src="profile-icon.png" alt="Profile" />
          </div>
          <div className="user-info">
            <p><strong>Username:</strong> {user?.email || 'Loading...'}</p>
            <p><strong>User Type:</strong> {user?.userType || 'Loading...'}</p>
            <Link to="/post-item">
              <button className="profile-btn">Post an Item</button>
            </Link>
            <Link to="/update-account">
              <button className="profile-btn">Update Account</button>
            </Link>
            <button className="profile-btn" onClick={handleLogout}>Log-out</button>
            <button className="profile-btn delete-btn" onClick={handleDeleteAccount}>Delete Account</button>
          </div>
        </div>

        {/* Right Content: Displaying Posted Items */}
        <div className="profile-content">
          <h2>Your Posted Items</h2>
          {error && <p className="error">{error}</p>}
          <div className="posted-items">
            {postedItems.length === 0 ? (
              <p>No posted items found.</p>
            ) : (
              postedItems.map((item) => (
                <div key={item._id} className="posted-item">
                  <img
                    src={`http://localhost:5000/${item.image}`}
                    alt={item.description}
                    className="item-image"
                  />
                  <p><strong>Description:</strong> {item.description}</p>
                  <p><strong>Category:</strong> {item.category}</p>
                </div>
              ))
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProfilePage;
